css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("giveawayoftheday.com") {

#container>div>div>div>div>div>div>div>div {
	 	width:100% !important;margin-top:-20px !important;
}

.credit,.text-container>div>p>span,.ulb,.recent-post{display:none !important;}

div#agb,#description>p>span {
	display:none !important;
}

.webring{padding:0px !important;}
.text-container{margin-top:-20px !important;
}
#container > div > div > div > div > div > div > div > div > div > div>div>div>div{
  display:none !important;
}
.webring>ul{
  text-align:center !important;
}


h1{text-align:center !important;
}


body {
  width:100% !important;
  font-family: candara,Verdana, sans-serif!important;
 
}

.text-container>div>p{width:77% !important;
}


div#more_titles{
	display:none !important;
}


.left{float:right !important;
}


div.tab_first {
  display:none !important;
}

div.tab_end_first {
  display:none !important;
}


div.tab {
  display:none !important;
}

div.tab_end {
  display:none !important;
}


img.left {
  display: block;
  float: right !important;
  margin-right: 20px;
  margin-bottom: 10px;
  border: none !important;
  z-index:1100 !important;
}
#container > div > div > div > div > div > div > div > div > div > div > div > a > img{display:none !important;}

.feedback {
  display:none !important;
}

div.post {
  display:none !important;
}

div.comments {
  display:none !important;
}


div.post p {
  display:none !important;
}

img.more {
  display:none !important;
}


#wp-calendar {
 display:none !important;
}


div#sidebar *{
  display:none !important;
}



div#partner_links {
  display:none !important;
}


.feeds {
  display:none !important;
}


.text-container>div{width:100% !important;
}

.text-container>div>div{width:49% !important;float:right !important;margin-top:auto !important;border:none;padding-bottom:20px !important;
}

div.alignright {
  display:none !important;
}

div.alignleft {
  display:none !important;
}


.contactleft {
 display:none !important;
}

.contactleft p {
  margin: 0;
}

.contactright {
  display:none !important;
}

.contacterror {
  display:none !important;
}


}
