css:
@-moz-document url-prefix("https://swing.service-now.com/") {
 .sort-columns, .navbar-title-caption, .navbar-title-display-value, .sorted, .icon-menu, .ng-binding, .icon-search, .navtree-item, .navtree-arrow, .info, .record-field, .navtree-item>label, .navtree-children>li>a, .nav-bar-header>header, .list_title, .list_search_title, .sn-card-component, .sn-widget-list-table-cell, .sn-widget-textblock-body, .label-text, .list_row_number_input
  {
    color: #dddddd !important;
  }

  .col-control-search, .ng-scope, .section_header_content_no_scroll, .table-column-header, .sn-form-stream-init, .sn-stream-section, .search-results-container, .search-bar, .nav-bar-container, .list_hdr, .col-control, .list_span_related, .vt, .list_decoration_cell, .text-align-right, .custom-form-group, .custom-form-group>table, .horizontal-rule, .horizontal-rule>hr, .popover, .header-button, .popover-title, .filterToolbar, .filterContainer, .list_filter, .modal-body, .modal-header, .modal-content, .section, .list_row, .table-bordered>tbody>tr>td, .table-bordered, #favorite-title, #filter, .list_header_search_row>td, .list2_no_records, .list2_body, .veditor_body, .iotd, .sla_timeline_header, .attachmentRow, .modal-body>rendered_body>table>tbody>tr>td>form>table, #attachment_dialog_list, #attachment_dialog_list>table>tbody>tr>td, .modal-body>rendered_body>table>tbody>tr>td>form>table, #current_attachments>div>table, #please_wait, #attachment_table_body>tr, .vt>table>tbody>tr>td, #notification_search_top, .notifications-main, .sn-widget-list_v2
  {
    background-color: #323f50 !important;
    color: #dddddd !important;
    border: none !important;
  }

  .navbar, .form-control, .sn-pane, .btn, .list-bottom, .filerTableSelect>a, .list_table_clone, #nav_edit_favorites, .sn-aside-body, .tab-aside, .context_item
  {
    background-color: #182838 !important;
    color: #dddddd !important;
    border: none !important;
    margin: 0px !important;
  }

  .h-card, .navbar-right, .navbar-right>span>button, .breadcrumb_container, html, body, .navbar-divider, .navigator-filter-form, .tab_caption_text
  {
    background-color: #182838 !important;
    color: #dddddd !important;
    border: none !important;
  }

  a
  {
    color: #008aff !important;
  }

  .list_nav
  {
    border-bottom: 10px solid #323f50 !important;
  }

  .list_nav_bottom
  {
    margin: 0px !important;
  }

  .navpage-main
  {
    border-left: 2px solid #182838 !important;
  }

  .list_header_cell, .col-control
  {
    background-color: #182838 !important;
    color: #dddddd !important;
    height: 36px !important;
  }

  .list_decoration_cell
  {
    background-color: #323f50 !important;
  }

  .list_nav_top, .list_nav_top_border
  {
    border-bottom: 20px solid #182838 !important;
  }

  #favorite-title
  {
    margin-left: 40px !important;
    margin-bottom: 20px !important;
  }

  .list_header_search_row
  {
    display: none !important;
  }

  .outputmsg_container
  {
    margin: 0px !important;
    border: none !important;
    background-color: #182838 !important;
  }

  .outputmsg
  {
    background-color: #323f50 !important;
    color: #dddddd !important;
    margin: 0px !important;
    border: none !important;
  }

  #close-messages-btn
  {
    margin: 5px !important;
  }

  #loadFileXml
  {
    background-color: #182838 !important;
    color: #dddddd !important;
    border: none !important;
    border-radius: 2px !important;
  }

  .tabs2_tab
  {
    background-color: #182838 !important;
    color: #dddddd !important;
    border-left: 1px solid #182838 !important;
    border-right: 1px solid #182838 !important;
    border-bottom: 1px solid #182838 !important;
    padding: 5px !important;
    margin: 0px !important;
    height: auto !important;
  }

  .ac_header
  {
    background-color: #323f50 !important;
    color: #dddddd !important;
    border: none !important;
    font-weight: normal !important;
    text-align: center !important;
  }

  .ac_dropdown, .ac_table_completer, .context_menu
  {
    background-color: #182838 !important;
    color: #dddddd !important;
    border: 1px solid #323f50 !important;
    /* OPTIONAL SHADOW FOR DROPDOWN/CONTEXT MENU */
    -webkit-box-shadow: 0px 0px 17px 7px rgba(0,0,0,0.57);
    -moz-box-shadow: 0px 0px 17px 7px rgba(0,0,0,0.57);
    box-shadow: 0px 0px 17px 7px rgba(0,0,0,0.57);
  }

  .ac_highlight
  {
    background-color: #182838 !important;
    color: #008aff !important;
    border: none !important;
    cursor: pointer !important;
  }

  .context_menu_hover
  {
    background-color: #323f50 !important;
    color: #008aff !important;
    border: none !important;
    cursor: pointer !important;
  }

  .context_item_hr
  {
    border-top: 1px solid #dddddd !important;
  }

/* APPLE ICON */
/* In order to use use the tree icon in snow. */

.icon-tree
{
    background-image: url("https://logodix.com/logo/19234.png");
}

.icon-tree:before
{
    content: none;
}

/* Search bar */
.sn-frameset-header #sysparm_search.focus
{
    background-color: #405264 !important;   
}


/* Button's at Incident page */

.btn-group>* {
    background-color: #323f50 !important;
    border-right: 1px solid #182838 !important;
}

.btn-group>*:hover {
    background-color: #405264 !important;
    border-right: 1px solid #182838 !important;
}

.form_action_button{
    background-color: #323f50 !important;
    border: 1px solid #182838 !important;
}

.form_action_button:hover{
    background-color: #405264 !important;
    border: 1px solid #405264 !important;
}
}
