css:
@-moz-document url-prefix("https://moj.esdnevnik.rs/") {
* {
    color: white !important;
}
.top-bar .logo a .logotype::before {
content: "e-Gnevnik" !important;
color: white;
}
.top-bar .logo a .logotype {
    color: #111111 !important;
     text-shadow: none !important;
}
.top-bar .logo a .logotype small {
display: none;
}
.side-bar {
background-color: #111111;
width: 110px !important;
}
body, .events .event .event-timeline .event-meta {
background-color: #080909;
}
.events .event .event-data .cloud, .grades-absents-wrap .courses-wrap .grades-absents, .grades-absents-wrap .stats .stats-grade-absent, .grades-absents-course-wrap .grades-absents, .grades-absents-course-wrap .grades-absents .grades-absents-content .absents-course-wrap .part-grades .part-grade, .grades-absents-course-wrap .grades-absents .grades-absents-content .schoolyear-part .part-grades .part-grade, .grades-absents-course-wrap .grades-absents .grades-absents-content .absents-course-wrap .part-grades .part-grade:nth-child(odd), .grades-absents-course-wrap .grades-absents .grades-absents-content .schoolyear-part .part-grades .part-grade:nth-child(odd), .courses-teachers-wrap .course-wrap {
background-color: #0c0c14;
border-radius: 20px;
}
.dropdown-wrap .dropdown,.dropdown-wrap .dropdown li a {
border: none;
}
.dropdown::-webkit-scrollbar-track {
	-webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3);
	border-radius: 20px;
	background-color: black;
}
.dropdown::-webkit-scrollbar
{
	width: 12px;
	background-color: #0c0c14;
}
.dropdown::-webkit-scrollbar-thumb
{
	border-radius: 20px;
	-webkit-box-shadow: inset 0 0 6px rgba(0,0,0,.3);
	background-color: #0c0c14;
}
.side-bar .student.active {
background-color: black;
border-radius: 40px;
margin-left: 5px;
margin-top: 20px;
}
.side-bar .student {
background-color: #3e3e3e;
border-radius: 40px;
margin-left: 5px;
margin-top: 20px;
}
.filter-bar {
background-color: #080909;left: 120px;
}
.top-bar, .dropdown-wrap .dropdown,.dropdown-wrap .dropdown li a {
background-color: #111111;
border-radius: 20px;
}
.grades-absents-course-wrap .grades-absents .grades-absents-content .absents-course-wrap:before, .grades-absents-course-wrap .grades-absents .grades-absents-content .absents-course-wrap:after, .grades-absents-course-wrap .grades-absents .grades-absents-content .schoolyear-part:after,.courses-teachers-wrap .course-wrap .teachers:before, .courses-teachers-wrap .course-wrap .teachers:after, .courses-teachers-wrap .course-wrap .teachers .teacher:after, .courses-teachers-wrap .course-wrap .teachers .teacher:before, .grades-absents-course-wrap .grades-absents .grades-absents-content .absents-course-wrap:before, .grades-absents-course-wrap .grades-absents .grades-absents-content .schoolyear-part:before {
background: white;
}
.side-bar .student.active:after {
    display: none;
}
.top-bar ul.top-navigation>li>a.router-link-exact-active {
	background-color: #000000;
}
.top-bar ul.top-navigation>li:hover>a {
background-color: black;
}
/* TELEFON KANCER CSS */
}
