css:
@-moz-document domain("www.facebook.com") {
/* Login */
#pageheight,
.WelcomePage_MainSell,
.WelcomePage_MainSellCenter{display:none !important;}
#menubar_container{background:auto) !important;}
/* General Changes */
.fb_menu[id=fb_menu_friends],
.fb_menu[id=fb_menu_home],
.fb_menu[id=fb_menu_profile],
#pagefooter,
#sidebar_ads{display:none !important;}
#fb_menubar{width:850px !important;}
#menubar_container{width:935px !important;}
.UIStandardFrame_Container{width:920px !important;}
/* Home */
#home_filter_list,
#home_sponsor_nile,
#pymk_hp_box,
.UIConnectWithFriends{display:none !important;}
#home_left_column{width:570px !important;}
/* Pages & Profiles */
#box_app_2356318349,  /* Page fans */
#box_app_2407511955,  /* Mutual Friends */
#boxes_left_moveable,  /* Removes third-party apps, has issues with pages */
#profile_blurb,
.secondary_actions{display:none !important;}
.right_column_container{width:580px !important;}
}
/* Login Width*/
@-moz-document url("http://www.facebook.com/"), url-prefix("http://www.facebook.com/index"), url-prefix("http://www.facebook.com/login") {
#fb_menubar{width:964px !important;}
#menubar_container{width:964px !important;}
}
