css:
@-moz-document url-prefix("https://learn.brock.ac.uk") {
body {
    background: #000;
}

section,
.span12 div,
#region-main div,
.bodyContent,
.sideBar,
.modal-body,
.tour-tour,
.studentView div,
.currentselected,
.cell > a,
td,
.dropdown-menu,
.popMenu,
.sideBarWrapper,
.emilyMessage *,
.levelodd, .cell,
.ygtvcell a,
.mdl-left a,
.moodle-dialogue-bd,
#tinymce{
    background: #333!important;
}

* {
    color: #fff;
}

.notification {
    background: #222!important;
}

a,
a:visited,
.welcome_message > a {
    color: #ccc;
}


a:active,
a:hover,
.welcome_message > a:hover {
    color: lightBlue;
}

.leveleven {
    background: #3a3a3a!important;
}

/*
 ______________________________
/ Why am I doing this?         \
\ This is way too much effort. /
 ------------------------------
        \   ^__^
         \  (oo)\_______
            (__)\       )\/\
                ||----w |
                ||     ||
*/

.dropdown-menu > ul > li > a,
.dropdown-menu > li > a,
.text > span,
.subject,
.title > h2,
.leaf > a,
.icon_content,
.levelodd,
.column > a,
.footer > a,
.event > a,
.date > a,
.cell, 
.cell > a,
.current > a,
.notice > a,
.minicalendar > caption > h3 > a,
h2.title > a,
.fitem > div,
.fheader,
legend,
.sectionname,
.fp-path-folder-name,
.ygtvcell a,
#id_message_path_row * {
    color: #fff!important;
}

.forumheaderlist, .block, .footer>div>a {
    background: #444!important;
    color: #fff!important;
}

.discussion > td,
.discussion > td > a,
.content > .no-overflow > p > span {
    background: #333!important;
    color: #fff!important;
}

h2.title > a:hover {
    color: #bbb;
}

.yui3-datatable-data * {
    color: black;
}

div.page-header-headings {
    margin-left:100px;
}
}

@-moz-document url("https://learn.brock.ac.uk/launchpage.php/"), url("https://learn.brock.ac.uk/launchpage.php") {
.brockLogo {
    overflow: hidden;
    visibility: hidden;
}

.whiteBG {
    max-height: 80px;
    background: #111!important;
}

i,
span {
    color: #ddd!important;
}

#studySkills > span {
    display: none;
}

#studySkills:after {
    content: "Study Skills";
}

#studySkills,
studentView > a > li > span {
    color: #ddd!important;
}

div.loaderContainer {
    background: #000!important;
}



.rssFeed > .rssItem {
    background: #333;
}
.rssFeed > .rssItem:nth-child(odd) {
    background: #444;
}

.rssFeed > .rssItem > .itemTitle > a {
    color: #fff;
}
}

@-moz-document url-prefix("https://learn.brock.ac.uk/mod/chat/gui_ajax/index.php?") {
body,
span,
div,
.time {
    background: #333 !important;
    color: #fff!important;
}

#choosetheme,
.yuimenu > *,
.yuimenuitem > a {
    color: #fff !important;
    background: #524D47 !important;
}

input {
    background: #555 !important;
    color: #eee!important;
}

.triangle-border {
    width:calc(100% - 180px);
}

.triangle-border.right {
    border-color: blue;
}


}

@-moz-document url-prefix("https://students.brock.ac.uk") {
body, .block-header, .block {
    background: #333!important;
}

* {
    color: #fff!important;
}

img[src*="/Images/Brockstar.gif"], img[src*="/images/headerlogo.gif"] {
    filter: invert(80%);
}

.nav > li > a:hover {
    background: #111!important;
}

td {
    background: #333!important;
}

input, .btn, textarea {
    background: #555 !important;
    color: #eee!important;
}

.active > a, thead > tr, .tt, .span6 {
    background: #444!important;
}

.alert {
    background: #bb9003;
}

.week-view .tt[style$="height: 97.47px"] {
    height: 100px !important;
}
}

@-moz-document url-prefix("https://students.brock.ac.uk/Account/Login") {
.control-label {
    width: 100px !important;
}

.controls {
    margin-left: 120px !important;
}

input {
    width: 120px !important;
}

.span6 h5 {
    min-height: 50px !important;
}

.control-group {
    height: 38px !important;
}
}
