@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(photobucket.com){

BODY{overflow-x:hidden !important;}

div.scrollbox {height:100; width:200; border-style: double hotpink; overflow:scroll}

.top,.bottom {width:99% !important; height:10px !important;}

.slideshow,.containerPageNav {border-width:1px !important; border-style:double !important;
border-color:hotpink !important; margin-left:5px !important; margin-right:5px !important;}

#cellWelcome {padding-left:60px !important; width:200px !important;}

.breadcrumb{margin-top:-60px !important;}

#tblAdUpload{margin-top:0px !important;}

#uploadPanelContainer,#panelSubAlbums{width:900px !important;}

#panelUploader_Email {display:none !important;}

#panelUploader_Ask {display:none !important;}

#tblAdUpload input.floated {margin-left:200px !important; margin-top:-50px !important;}

#tblAdUpload #addMoreFiles1

{margin-left:600px !important; margin-top:-40px !important;}

.txtCode {width:100px !important;}

.editable,#submitUpload1{font-size:small !important;}

#selectedOptions,#tblAdUpload ,fine_print {height:10px !important;}

#formFullView {margin-left:80px !important; margin-top:-27px !important;}

#tblUpload{margin-bottom:7px !important;}

#cellLogo,IMG[src$="folder.gif"],#panelHeaderAd,#menuTop,

#containerAlbumButtons,#uploadPanel>TBODY>TR:first-child,

#advPanelContainer,#panelFooter,#containerAlbumPromo,

#containerSqAd,.breadcrumbLabel,#uploadPanel H2,.inline+BR,

.panelMessage,.panelError,#cellAd,#linkBuyPrints,span>.menuItemSep

{display:none !important;}

}
