css:
@-moz-document domain("dice.skyjedi.com") {
body {
    background-color: #918b83;
    margin-left: 7px;
}

.destiny-box {
    background-color: #c6c4b1;
}

.messagebox, .App {
    width: 600px !important;
    max-height: 50vh !important;
}

.messagebox>*>.btnAdd, .messagebox>.btnAdd {
    position: absolute !important;
    max-height: initial !important;
    bottom: 20px !important;
}

.message {
    width: auto;
}

.left, .destiny-box {
    max-width: 600px;
}

.right {
    margin-left: 630px;
}

.message>button {
    color: #7b3b3b !important;
}
}
