css:
@-moz-document url-prefix("https://buff.163.com/") {
/*CSGO Page + Others*/
/*Outer Background*/
body {
    background:  linear-gradient(0deg, #28292b, #192033), url(https://buff.163.com/static/images/site/header-bg.jpg)no-repeat center top;
    background-blend-mode: difference
}
/*.market-list {
    background: linear-gradient(0deg, #28292b, #192033);
}*/
/*Inner Background*/
.market-card {
    background: #11151c;
}
/*Item Border*/
.list_card li {
    border: 1px solid #4773c8;
    background: #11151c;
}
/*Item Text*/
.list_card a:link, .list_card a:visited {
    color: #e4e4e4;
}
/*Hovered/Selected Item*/
.list_card li.on, .list_card li:hover {
    border-color: #EEA20E;
    background: #11151c;
}
/*Page Navigation*/
.pager li * {
    border: 1px solid #45536C;
    color: #45536C;
}
.pager li.disabled * {
    border-color: #45536C;
    background: #45536C;
    color: #141820;
}





/*Sellers Shop*/
.shop-list .list_card {
    background: #12161e;
}
.list_card li {
    border: 1px solid #12161e;
    background: #12161e;
}
.user-wallet .w-Tag_big span {
    color: #e4e4e4;
}
.pay-way li {
    color: #e4e4e4;
}
.shop-list .card-pager {
    padding: 30px 0;
    background: #181c23;
}





/*Top up etc.*/
.user-wallet {
    background: #181c23;
}
.user-pay-desc {
    background: #181c23;
}
.list_tb th {
    background: #181c23;
}
.list_tb td, .list_tb th {
    border-bottom: 1px solid #181c23;
}
.user-record {
    background: #181c23;
}
.list_tb td {
    background-color: #181c23 !important;
}
.list_tb th {
    background: #272e39 !important;
}
/*Account*/
.user-setting {
    background: #181c23;
}
.user-feedback {
    background-color: #181c23;
}
/*Cancel PopUp Window*/
.popup .popup-cont {
    background-color: #272e39;
}
.popup .popup-btns {
    background-color: #272e39;
}
.popup .popup-cont h2 {
    color: #EEA20E;
}




/*News*/
.news-cont {
    background: #1c202b;
}
.news-list li:hover {
    background: #4773c8;
}
.news-list li {
    background: #1c202b;
}
.news-detail-header h1 {
    color: #e4e4e4;
}
.news-detail-cont p {
    color: #e4e4e4;
}
.a_Black:link {
    color: #e4e4e4;
}







/*Listed Item Page*/

/*Not Loading Background*/
.detail-tab-cont {
    background: #28292b;
}
.list_tb th {
    background: #28292b;
}
.list-pager {
    background-color: #28292b;
}
.list_tb td {
    background-color: #28292b;
}
/*Sticker Background*/
.csgo_sticker .stickers img, .csgo_sticker_inline .stickers img {
    background-color: #181c23;
}
/*Item Borders*/
.list_tb td, .list_tb th {
    border-bottom: 1px solid #37383c;
}
/*Item Name Table*/
.name-cont h3 .textOne {
    color: #e4e4e4;
}
/*Seller Name*/
a:link {
    color: #e4e4e4;
}
/*Description*/
.desc_content {
    border: 1px solid #222222;
    background: #222222;
    color: #e4e4e4;
}
.desc_arrow_up {
    filter: brightness(14%);
    background: url(https://buff.163.com/static/images/icon_desc_arrow.png) no-repeat;
    background-size: contain;
    width: 18px;
    height: 8px;
    position: absolute;
    margin-top: -31px;
    margin-left: 45px;
}
.desc_dialog {
    filter: invert(1);
    filter: brightness(2.6);
    background: url(https://buff.163.com/static/images/icon_desc_dialog.png) no-repeat;
    background-size: contain;
    width: 14px;
    height: 14px;
    position: absolute;
    margin-left: 43px;
    margin-top: -15px;
}
/*Trade Records*/
body {
    font: 14px/1.2em Microsoft Yahei,"\5FAE\8F6F\96C5\9ED1","Hiragino Sans GB","Hiragino Sans GB W3",arial;
    color: #e4e4e4;
}
.popup-header h2 {
    color: #EEA20E;
    background-color: #33363a;
}
.popup-header {
    background: #33363a;
    border-bottom: 1px solid #EEA20E;
    color: #EEA20E;
}
/*Gallery*/
.pinterest-item {
    border: 1px solid #4773c8;
    background: #33363a;
}
.pinterest-user {
    background: #33363a;
}




/*Home Page*/
.area1 {
     background: #afafaf url(https://buff.163.com/static/images/site/index-section1-bg.jpg) no-repeat center top;
     background-blend-mode: difference;
}
.area3 {
    background: #afafaf url(https://buff.163.com/static/images/site/index-section1-bg.jpg) no-repeat center top;
     background-blend-mode: difference;
}

}
