@-moz-document domain("twitter.com") {
/*  ~ Pastel Monstrosity ~ by  */
/* Changes default color of sidebar Tweet button, small Tweet button at top of page, & video play button bg */
.r-1qqlz1x {
    background-color: rgba(141, 174, 255, .871)!important;
}

/* Changes hover color of sidebar Tweet button, small Tweet button at top of page, & video play button bg */
.r-1waj6vr {
    background-color: rgba(168, 141, 255, .871);
}

/* Changes "What's happening?" text styling & color */
.public-DraftEditorPlaceholder-root {
    color: rgba(224, 215, 255, .761);
    position: absolute;
    z-index: 1;
    font-family: Franklin Gothic Book;
    font-size: 18px;
}

/* Allows customization of border color around timeline tweets & sidebar */
.css-1dbjc4n:not(.r-xnswec):not(.r-hjejmn) :not(.r-urgr8i):not(.r-m5arl1):not(.r-42olwf):not(.r-1f0042m) {
    border-color: rgba(233, 177, 255, .239);
}

/* Changes colors & font of links & hashtags, not including the sidebars */
.r-xfsgu1 {
    color: rgba(76, 128, 255, .639);
    font-family: Franklin Gothic Book!important;
}

/* Changes color of Twitter bird in top left corner & Verified checkmarks */
.r-13gxpu9 {
    color: rgb(149, 244, 255)!important;
}

/* Recolors all symbol icons on the site, such as the Like heart */
.r-yyyyoo:not(.r-13gxpu9):not(.r-xnswec):not(.r-hjejmn):not(.r-urgr8i):not(.r-m5arl1):not(.r-11mmphe):not(.r-1n0xq6e) {
    fill: rgb(255, 177, 237);
}

/* Changes color of vertical line indicating tweet threads */
.r-vrwoeq {
    background-color: rgb(224, 215, 255);
}

/* Changes color of Retweet number text after clicking Retweet */
.r-nw8l94 {
    color: rgb(160, 233, 209);
}

/* Changes color of Likes number text after clicking Like */
.r-daml9f {
    color: rgb(160, 169, 233);
}

/* Changes right sidebar color */
.r-1u4rsef {
    background-color: rgb(251, 245, 255);
}

/* Changes right searchbar color */
.r-e84r5y {
    background-color: rgb(251, 245, 255);
}

/* Changes font color for most of the page */
.r-hkyrab {
    color: rgb(50, 36, 114);
}

/* Changes color of alternate text such as @usernames and Copyright info */
.r-1re7ezh {
    color: rgba(152, 69, 230, .651);
}
}
