css:
@-moz-document domain("banid.pl") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 10px;
             background-color: #212121;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 10px;
             background-color: #212121;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 10px;
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, .3);
             background-color: #111;
        }

body {
    background: #000
}
input, textarea, .lastpost, .windowbg.stats, .info, .windowbg.children, .windowbg.icon, #header, select, .upperframe.clear, .upperframe.clear > span, .roundframe, .lowerframe > span, .lowerframe, .upperframe > span, .upperframe, .dropmenu li li, .centertext.content, .topslice > span, .topslice, .content, .botslice > span, .botslice, .post_wrapper, #display_jump_to, #topic_icons, .description.windowbg, .padding, .description, td, #personal_messages .capacity_bar  {
    background: #111 !important;
    border-color: #222 !important;
    color: #fff !important;
}
h1, h2, h3, h4, h5, h6, .dropmenu li a.firstlevel span.firstlevel, .active.button_strip_view_all_members > span, .buttonlist ul li a.active span {
    color: #fff;
}
.floatleft, h4.catbg, h4.catbg2, h3.catbg, h3.catbg2, .table_list tbody.header td.catbg, .dropmenu a, h3.catbg a:link, h3.catbg a:visited, h4.catbg a:link, h4.catbg a:visited, h3.catbg, .table_list tbody.header td, .table_list tbody.header td a, #header .user ul li.greeting, p#whoisviewing, .navigate_section ul li, .dropmenu li li a span {
    color: #c6c6c6;
}
.poster h4, .poster h4 a, h4 > span:nth-of-type(1) {
color: #c6c6c6 !important;
}
.lastpost > p > a, [href^="https://banid.pl/forum/index.php"] {
    color: #d20202 !important;
}
.button_submit {
    background: #750101 !important;
}
}
