css:
@-moz-document domain("bonjwa.de") {
#block-views-parallax-block {
    filter: grayscale(70%);
}
p > a, ol > li > a {
    text-shadow:
   -1px -1px 1px #000,  
    1px -1px 1px #000,
    -1px 1px 1px #000,
     1px 1px 1px #000;
}
.content, .c-content, table, .table-striped > tbody > tr:nth-of-type(2n+1), .panel, .dropdown-menu, .dropdown-menu > li > a, .c-divider, .c-slider, .navbar-inverse, .tabs--primary li.active a, .tabs--primary li.active a:active, .tabs--primary li.active a:focus, .tabs--primary li.active a:visited, nav .mlid-7730.active > a, nav .mlid-7730.active > a:hover, nav .mlid-7730.active > a:focus{
    background: grey !important;
    color: white;
}
table th, table > thead > tr > th, .table th, .table > thead > tr > th, body, .panel-default > .panel-heading, .form-control, .webform-client-form .email, .navbar-inverse .navbar-nav > .active > a, .navbar-inverse .navbar-nav > .active > a:hover, .navbar-inverse .navbar-nav > .active > a:focus, hr, .navbar-inverse .navbar-nav > li > a:hover, .navbar-inverse .navbar-nav > li > a:focus, .panel-footer {
    background: #9e9e9e;
}
.btn-light, .form-control, .webform-client-form .email, .c-title {
    background: #9e9e9e;
    font-weight: 700;
    color: black;
}
.btn-light:hover {
    background: black;
    color: #9e9e9e;
}
.free-streaming-slot, .cancelled-streaming-slot {
    background: repeating-linear-gradient(
    	-30deg, lightgrey, lightgrey 5px, grey 5px, grey 10px 
    ) !important;
    background-repeat: repeat;
    background-attachment: scroll;
    background-image: repeating-linear-gradient(
		-30deg, grey, grey 5px, lightgrey 5px,
    	lightgrey 10px
    ) !important;
    background-size: auto;
    background-origin: padding-box;
    background-clip: border-box;
}
.panel-bonjwa > .panel-heading {
    background: #1E9761;
    color: white;
}
.panel-twitch .panel-footer {
    background: #9146FF;
}
}
