/* ❗❕ Underline from left on Hover ❕❗ */
.style-k2dqrb5trepeaterButton {
    display: inline-block;
    vertical-align: middle;
    -webkit-transform: perspective(1px) translateZ(0);
    transform: perspective(1px) translateZ(0);
    box-shadow: 0 0 1px rgba(0, 0, 0, 0);
    position: relative;
    overflow: hidden;
}

.style-k2dqrb5trepeaterButton:before {
    content: "";
    position: absolute;
    z-index: -1;
    left: 10px;
    right: 100%;
    bottom: 0;
    background: #fff;
    height: 2px;
    -webkit-transition-property: right;
    transition-property: right;
    -webkit-transition-duration: 0.3s;
    transition-duration: 0.3s;
    -webkit-transition-timing-function: ease-out;
    transition-timing-function: ease-out;
}
.style-k2dqrb5trepeaterButton:hover:before, .style-k2dqrb5trepeaterButton:focus:before, .style-k2dqrb5trepeaterButton:active:before {
    right:10px;
    left:10px;
}
