css:
@-moz-document domain("lukol.com") {
/* -=< Darker Lukol by Zigboom Designs >=- */


body {
    background-color: #181818 !important;
}

body * {
    border-color: transparent !important;
    color: #999 !important;
}

.gsc-webResult.gsc-result, .gsc-results .gsc-imageResult {
    background-color: transparent !important;
}

.top_menu {
    background: #282828 !important;
}

#search_container, .gsc-control-cse div.gs-no-results-result {
    background-color: #282828 !important;
    border: 3px solid #333 !important;
}

/* Padding for Search Results */

#___gcse_0 .gs-web-image-box, body .gs-promotion-image-box {
    padding: 2px 15px 2px 0px !important;
    margin-left: 190px !important;
}

/* Colors */

button.s_btn {
    background-color: #282828 !important;
}

.search_field_clear input#q, .search_field_clear input#q2 {
    color: #ccc !important;
}

#___gcse_0 .gsc-webResult.gsc-result, body .gsc-above-wrapper-area {
    border-color: transparent !important;
}

#___gcse_0 a.gs-title:link, #___gcse_0 a.gs-title:link b, #___gcse_0 .gsc-cursor-page, #___gcse_0 a.gsc-trailing-more-results:link, #___gcse_0_right a.gs-title:link, #___gcse_0_right a.gs-title:link b, #___gcse_0_right .gsc-cursor-page, #___gcse_0_right a.gsc-trailing-more-results:link {
    color: #ccc !important;
    transition: all 0.4s;
}

#___gcse_0 a.gs-title:visited, #___gcse_0 a.gs-title:visited b, #___gcse_0 a.gsc-trailing-more-results:visited, #___gcse_0_right a.gs-title:visited, #___gcse_0_right a.gs-title:visited b, #___gcse_0_right a.gsc-trailing-more-results:visited {
    color: #666 !important;
}

#___gcse_0 a.gs-title:hover, #___gcse_0 a.gs-title:hover b, #___gcse_0 a.gsc-trailing-more-results:hover, #___gcse_0_right a.gs-title:hover, #___gcse_0_right a.gs-title:hover b, #___gcse_0_right a.gsc-trailing-more-results:hover {
    color: #fff !important;
}

.top_menu > ul > li.active > a {
    color: #FFF;
    position: relative;
    border-top: 2px solid #fff !important;
}

/* Grayscale Images */

.logo a img, #footer > div > a:nth-child(1) > img, #loadbar > center > img, .logo img, body > center > a > img {
    margin-right: 5px;
    margin-left: 20px;
    filter: grayscale(1) invert(0.905) opacity(0.75);
/* 
    mix-blend-mode: multiply;
    filter: saturate(2) brightness(2);
*/
}

/* Ads */

body .gsc-adBlock iframe {
    filter: grayscale(1) invert(0.905);
}

body .gsc-adBlock {
    border-bottom: 1px solid #444 !important;
}

/* Footer */

#___gcse_0 .gsc-cursor-page, #___gcse_0_right .gsc-cursor-page {
    padding: .8em 1.1em;
    border: 1px solid;
    border-color: #282828 !important;
    background-color: #282828 !important;
    color: #ffffff;
}

#___gcse_0 .gsc-cursor-page.gsc-cursor-current-page, #___gcse_0_right .gsc-cursor-page.gsc-cursor-current-page {
    background-color: #444 !important;
    border-color: #444 !important;
}

#footer a, #footer a:hover, #footer a:visited {
    color: #ccc !important;
}

#footer > div.footer_top, #___gcse_0 .gs-result .gs-snippet {
    background: #181818 !important;
}




/* The End */
}
