 */


@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {
/* SOF */

	/* gtfo divs */
div#masthead-bar-contents > span#upload-button,
div#masthead-inner,
div#ad_creative_2 + div,
div#iyt-add-remove-module-div,
div#watch-other-vids,
div#watch-ratings-views,
div#watch-main-area,
div#watch-stats-data-wrapper,
div#watch-stats-data-wrapper + div,
div#footer, 
div#copyright {
	display:none !important; }

	/* fixit */
div#bar {
	display:block !important;
	position:fixed;
	top:0;
	z-index:20 !important;
	width: 960px !important; }

	/* some reconfiguring */
div#baseDiv {
	margin-top: 40px !important; }
.is-english #masthead .search-form {
	margin-right: 10px !important; }
div#video_grid div:nth-of-type(2n+4) {
	background: #E0E0E0 !important; }

div#watch-other-vids,
div#watch-ratings-views,
div#watch-main-area,
div#watch-stats-data-wrapper,
div#watch-stats-data-wrapper + div,
div#footer, 
div#copyright {
	display:none !important; }

div#watch-this-vid {
	position:fixed !important;
	left: 0 !important; }

div#watch-comment-panel {
	position:fixed !important;
	left: 650px !important;
	overflow: scroll;
	top: 40px;
	right: 10px;
	bottom: 10px;
	}

}
