}

.result__url:hover {
	text-decoration: none !important;
}

.result__title {
	transition: transform .3s;
    transition-timing-function: cubic-bezier(0.68, -0.55, 0.27, 1.55);
}

.result__extras__url {
	transition: transform .3s;
}

.result__title:hover, .result__extras__url:hover {
	transform: scale(1.1) translate(2px, 0px);
}


.result__snippet.js-result-snippet:hover {
	color: white !important;
}

.showcase__dropdown__list:hover {
    background-color: #46496d;
    border-radius: 10px;
}

.showcase__link.showcase__text{
	text-decoration: none;
}

.footer__card {
   -webkit-transition: all .3s ease-in-out;
}

.footer__card:hover {
    background-color: #26263a !important;
}

.footer__card__title:hover{
	text-decoration: none;
}

.notification is-showing {
   backdrop-filter: blur(10px);
}

.notification__text {
	background-color: #191c2b99;
	color: #d5a6ff;
}

.modal--dropdown--region .modal__header .frm__input {
 	background-color: #151523;   
}

.modal__list__link:hover, .modal__list__link.is-active, .modal__list__link.is-highlighted, .modal--dropdown--region .modal__list__link.is-highlighted, .highlight .result__image, .switch, .modal--dropdown--settings .settings-dropdown--section .frm__field .frm__switch .frm__switch__label.btn{
	background-color: #1a1a27;
}

