.header-wrap, .vertical--map--sidebar-left .has-requery .vertical--map__sidebar__header, .map-requery-mobile {
    box-shadow: 0px 2px 9px 1px rgba(0,0,0,0.75);
    border-radius: 0 0 0 40px;
}

.header-wrap:after {
	background: transparent;
}

.footer {
	box-shadow: 0px -2px 9px 1px rgba(0,0,0,0.75);
    border-radius: 40px;
}

.search:hover .search__clear, .search__input:focus ~ .search__clear, .search--header.has-text.search--hover .search__clear, .search--header.has-text.search--focus .search__clear, .search--home.has-text .search__clear {
    visibility: visible;
    opacity: 1;
}

.no-touch .btn--icon:hover, .no-touch .header__button:hover, .no-touch .header__button--menu:hover, .no-touch .header__button:focus, .no-touch .header__button--menu:focus {
    color: #0f0f16;
    background-color: #262638;
    transition: background-color .2s cubic-bezier(0.62, 0.01, 0.56, 1.01);
}

.header__button--menu:hover {
	color: white !important;
}

.nav-menu, .nav-menu--slideout {
    background-color: #07070ec4;
    backdrop-filter: blur(5px);
    border-radius: 20px 0 0 20px;
}

.nav-menu__item {
    text-decoration: none;
}

.nav-menu__item>a:hover {
    color: #9c60ff;
    text-decoration: none;
}

.modal.modal--popout.modal--popout--bottom-left.modal--popout--sm .modal__box {
    background: #1919278c;
    backdrop-filter: blur(10px);
}

.showcase__heading{
	text-decoration: none !important;
}

.showcase .showcase__dropdown-bottom {
	background: #24283e6e;
    backdrop-filter: blur(20px);
}

.dark-bg .showcase .showcase__dropdown-top .showcase__dropdown__list:hover {
	background: #31344e9c;
}

.module__toggle, .tile__expand {
    background-color: #1d1d2b;
}

.is-not-mobile-device .module__toggle:hover, .place-detail__cta-item:hover .place-detail__cta-icon {
background-color: #262638;
}

.hide--screen-xs>a:hover {
	text-decoration: none;
    color: #9c60ff;
}

.result a:hover {
	background: tranparent;
    border-color: transparent !important;
	text-decoration: none;
}

.module--carousel__items {
    background: #11111a;
}

.result--more__btn {
	border-radius: 45px;
    background: #1d1d2b;
    box-shadow: 0px 0px 15px 1px rgba(0,0,0,0.75);
    -webkit-transition: all .3s ease-in-out;
}


.result--more.is-loading {
	border-radius: 45px;
}

.btn.feedback-btn, .btn.feedback-btn a, .btn.feedback-btn a:hover {
	border-radius: 20px;
    background: #1d1d2b;
	-webkit-transition: all .3s ease-in-out;
}

.dark-header .footer__card {
	background: #11111a;
    border-radius: 10px;
}


.frm__select:hover select, .btn.is-active, .btn:hover, .frm__select:hover, .btn.most-visited__toggle:hover, .btn.most-visited__toggle:active, .btn.most-visited__toggle:focus, .no-touch .tile-nav--sm.can-scroll:hover, .no-touch .tile-nav--sm.can-scroll:active, .no-touch .tile-nav--sm.can-scroll:hover:active, .modal .btn:hover:not(.is-disabled), .modal .btn:hover:not(.is-disabled) {
    background-color: #37375d;
}

.module__more-at-bottom {
	transition: transform .3s;
    
}

.module__more-at-bottom:hover {
    text-decoration: none;
    color: #9c60ff;
    transform: scale(1.1) translate(10px, 0px);
}
}

@-moz-document url-prefix("https://duckduckgo.com/?q"), url-prefix("https://duckduckgo.com/?t"), url-prefix("https://duckduckgo.com/settings") {
.tile__media, .tile--img__media {
	border-radius: 10px 10px 0 0;
}


.html5-video-player:not(.ytp-transparent), .html5-video-player.unstarted-mode, .html5-video-player.ad-showing, .html5-video-player.ended-mode, .html5-video-player.ytp-fullscreen {
