}

.c-detail__title a:hover, .c-detail__more>a:hover {
	text-decoration: none;
    color: #9c60ff !important;
}

.html5-video-player:not(.ytp-transparent), .html5-video-player.unstarted-mode, .html5-video-player.ad-showing, .html5-video-player.ended-mode, .html5-video-player.ytp-fullscreen {
	
}

.is-expanded .detail__media__vid-wrap {
  border-radius: 20px;
  overflow: hidden;
}


iframe.detail__media__vid {
	background-color: transparent !important;
}


.tile {
    content: "";
    box-shadow: 0;
    border-color: transparent;
    background: transparent;
}

.highlight.tile, .highlight.tile--s, .highlight.tile--info, .is-selected.tile, .is-selected.tile--s, .is-selected.tile--info, .highlight.tile--f .tile--f__main, .highlight.tile--f .tile--f__alt, .is-selected.tile--f .tile--f__alt, .highlight.tile:active, .highlight.tile--s:active, .highlight.tile--info:active {
    border-color: transparent;
}

.tile, .tile__body {
	border-radius: 0 0 10px 10px;
    text-align: center;
}

.tile a:hover, .tile__body a:hover {
	background-color: #282828;
}

.metabar__dropdowns-wrap:after, .metabar__dropdowns-wrap:before {
    background: transparent !important;
}

.zci-wrap {
    background-color: #0e0e15;
}

.detail {
    background-color: #0e0e15;
    border-color: transparent;
}

.detail--xd .tile-nav--sm {
    background-color: #151523;
    color: #888;
}

.vertical--map__sidebar__wrapper, .is-mobile-device .vertical--map__sidebar--normal .vertical--map__sidebar-mask {
    background: #0e0e15;
}

.modal__header, .module__section, .module__section:first-child.place-detail__section--tab, .module__clickable-header {
