    background: #24283e;
}

.modal--dropdown--settings .settings-dropdown--section .frm__field .frm__select:before {
    background-image: linear-gradient(to right, rgba(40, 40, 40, 0) 0%, rgb(36, 40, 62) 65%);
}

.tile--img__sub {
	background: #1d1d2b;
    text-align: center;
    border-radius: 0 0 10px 10px;
}

.tile--img__title:hover, .tile--img__domain:hover {
	color: #bbb9bf;
}

.tile--img__sub:hover {
	text-decoration: none;
}

.tile a:hover {
	background: #37375d;
    transition: cubic-bezier(0.4, 0, 0.2, 1);
}

.tile .tile__title a:hover {
	text-decoration: none;
}

.set-bookmarklet__input, .set-bookmarklet__data, .btn, .btn:visited, .btn:active, .btn.btn--primary, .btn.btn--secondary, .btn--primary:hover, .btn.is-disabled, .btn.is-disabled:hover, input, textarea, .frm__input, .frm__text, .detail--xd .c-detail__btn, .set-bookmarklet, .set-reset, .frm__select, .frm__select select, .zci--json_validator textarea, .colorpicker, .feedback-modal__submit.is-disabled, .feedback-modal__submit.is-disabled:hover, .feedback-modal__submit.is-disabled:active, .feedback-modal__submit.is-disabled:focus, .most-visited__toggle, .btn.btn--skeleton:hover {
	background: #1d1d2b;
}

