css:
/* Use smaller font */
#fbPanelBox .panelTab-text, #fbPanelBox .toolbarbutton-text
  { font-size:xx-small !important; }

/* Eliminate useless navigation arrows */
#fbNavigateBackButton, #fbNavigateForwardButton, #fbNavigationSeparator
  { display:none !important; }

/* Eliminate side margin inside tabs */
#fbPanelBox .panelTab-left, 
#fbPanelBox .panelTab-right
  { display:none !important; }

/* Reset the border of tabs */
#fbPanelBox panelTab,
#fbPanelBox .panelTab-text,
#fbPanelBox *[selected="true"], 
#fbPanelBox *[selected="true"] .menuTarget
{
  -moz-border-radius-bottomleft: 0.9em !important;
  -moz-border-radius-bottomright: 0.9em !important;
  border-style:solid !important;
  border-width:0px 1px 1px !important;
}
#fbPanelBox .panelTab-text:hover,
#fbPanelBox *[selected="true"],             /* Drop down to the right */
#fbPanelBox *[selected="true"] .menuTarget  /* Drop down to the right */
  { border-color:rgba(128, 128, 128, .7) !important; }
#fbPanelBox panelTab,
#fbPanelBox .panelTab-text
  { border-color:rgba(128, 128, 128, .1) !important; }

/* Adjustment the side margin again */
#fbPanelBox *
{
  -moz-padding-start: 0 !important;
  -moz-padding-end: 0 !important;
  -moz-margin-start: 0 !important;
  -moz-margin-end: 0 !important;
}

/* i really want this to be global */
