css:
@-moz-document 

url-prefix(http://www.google.com/reader),
url-prefix(https://www.google.com/reader),
url-prefix(http://www.google.com/reader/shared),
url-prefix(https://www.google.com/reader/shared)

{

#main {
	font: 12px Tahoma !important;
}

a {
	text-decoration: none !important;
	color: #0097e5 !important;
}

a:hover {
	color: #d29e01 !important;
	background-color: #ffffd8 !important;
}

.overview-header {
	direction: rtl !important;
	text-align: right !important;
	margin-top: 20px !important;
}

.overview-segment .label {
	direction: ltr !important;
	text-align: left !important;
}

#rec-preview {
	direction: rtl !important;
}

.section-header {
	direction: ltr !important;
	text-align: center !important;
}

#recent-activity {
	direction: rtl !important;
	line-height: 170% !important;
}

.overview-stream h4 {
	text-align: left !important;
	direction: ltr !important;
}

.entry-main {
	direction: rtl !important;
}

#chrome-title {
	font: bold 23px Arial !important;
}

.entry-date {
	float: left !important;
	direction: ltr !important;
}

.entry-title {
	font: bold 17px Arial !important;
}

.entry-title-go-to {
	display: none !important;
}

.entry-annotations {
	direction: ltr !important;
}

.entry-author {
	float: left !important;
	direction: ltr !important;
}

.entry-via {
	text-align: left !important;
	direction: ltr !important;
}

.item-body {
	line-height: 180% !important;
	text-align: justify !important;
}

.item-body h1, .item-body h2, .item-body h3 {
	font: bold 15px Arial !important;
}

.item-body p {
	font-size: 13px !important;
}

.item-body ul li {
	list-style: square !important;
}

.item-body pre {
	background-color: #e9faff !important;
	border: 2px solid #d0f4fe !important;
	direction: ltr !important;
	text-align: left !important;
	color: #333 !important;
	padding: 5px !important;
}

.item-body blockquote {
	background-color: #e9faff !important;
	border: 2px solid #d0f4fe !important;
	color: #555 !important;
	padding: 5px !important;
}

.entry-comments {
	direction: rtl !important;
}

.conversation-attribution, .add-comment-container {
	direction: ltr !important;
	text-align: right !important;
}

.collapsed {
	direction: rtl !important;
	text-align: right !important;
}

.collapsed h2 {
	font: bold 12px Tahoma !important;
	float: right !important;
}

.collapsed .entry-date {
	float: right !important;
}

.collapsed:hover {
	background-color: #ffffd8 !important;
}

.collapsed .entry-title {
	color: #0097e5 !important;
}


}
