@-moz-document domain("web.roblox.com") {
#header, #navigation, .rbx-navbar-search, .dialog-header, .dialog-body, .dialog-main, .dialog-container, .chat-header, .chat-body, .chat-main, .section-content, .rbx-tab-heading, .nav-tabs, stack, .stack-list, .stack-row, .popover, .interaction-container, .dark-theme .messages .messageDivider, .avatar-card, .avatar-card-container, .avatar-card-btns, .notification-stream-body, #notification-stream-scrollbar, .notification-stream-header, .notification-stream-container, .notification-content-view, #notification-stream-body, .notification-content-view, .roblox-popover-container, .notification-stream-header, .stream-banner, .banner-new,  .chat-search, .menu-vertical, textarea, .dialog-container .dialog-input-container, .rbx-navbar-search-string,.rbx-navbar-search-text, .modal-body, .modal-content, .btr-game-button, .dialog-main, .dialog-container {
   
    background: #ff000000!important;

        backdrop-filter: blur(3px)!important;    

    
}
.text-description {
 color: #fff!important;   
}


.section-content-off {
       background: #ff0000!important;
    
}

.dark-theme .rbx-header .navbar-search {
        background: #ff000000!important;

        backdrop-filter: blur(3px)!important;    
  -webkit-animation: glow 1s ease-in-out infinite alternate;
  -moz-animation: glow 1s ease-in-out infinite alternate;
  animation: glow 1s ease-in-out infinite alternate;
}

@-webkit-keyframes glow {
  from {
    box-shadow
: 0 0 10px #fff, 0 0 0px #fff, 0 0 0px #ffffff, 0 0 10px #ffffff, 0 0 0px #ffffff, 0 0 20px #ffffff, 0 0 0px #ffffff;
  }
  
  to {
    box-shadow
: 0 0 10px #fff, 0 0 0px #fff, 0 0 0px #ffffff, 0 0 0px #ffffff, 0 0 0px #ffffff, 0 0 20px #ffffff, 0 0 0px #ffffff;
}
}
.btr-game-button {
    border: 0px solid #b8b8b8!important;
        backdrop-filter: blur(3px)!important;
}
#header {
      box-shadow: 0 0 8px 0 rgba(0,0,0,.3);   
}
 .rbx-navbar-search-option, .rbx-navbar-search-anchor, .dropdown-menu, .rbx-navbar-search-option,  .rbx-clickable-li, .rbx-navbar-search-option, .rbx-clickable-li, .btr-fastsearch, .rbx-navbar-search, .rbx-navbar-search-option.selected, .dropdown-menu, li  {
      background: #ff000000!important;
    backdrop-filter: blur(3px)!important;          
          }

.dialog-message  {
background-color: #11121494!important;  
        border: 0px!important;  
}
.rbx-header {
 border-width: 0 0 0px!important;   
}

.banner-error {
 display:none !important;    
}
body {
 background-image:url("https://wallpapercave.com/download/ocean-waves-wallpapers-wp2762332");
     background-repeat: repeat-y;
