    background: #ff000000!important;
    backdrop-filter: blur(3px)!important;
    border-color: hsla(0, 0%, 100%, .2);
    color: #bdbebe;
    border-radius: 8px;
}
.people-list, .container-list {
    border-radius: 25px!important;  
        box-shadow: 0 0 11px 0 rgba(0,0,0,.3);
    width:106%;
