css:
@-moz-document domain("apynews.pl"), domain("instagram.com") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 2px;
             background-color: #212121;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 5px;
             background-color: #212121;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 2px;
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, .3);
             background-color: #a83b3b;
        }

body, .navbar-default, .navbar-custom,.container-post, .container, .facebook-custom-likes, .facebook-custom-likes iframe, .container-post .row-similar-posts .polecane-tile, .container-quiz .row-similar-posts .polecane-tile, .container-wywiad .row-similar-posts .polecane-tile, .Embed, .container-quiz .column-quiz .form-group, .container-quiz .column-quiz .form-group .radio>label, .container-quiz .column-quiz .form-group .radio, form, .btn-wrapper .btn-custom, .btn-wrapper .btn-custom-link {
    background: #111;
    border-color: #111;
}
.navbar-default .navbar-collapse .navbar-nav .dropdown>.dropdown-menu.dropdown-menu__rankings, .navbar-custom .navbar-collapse .navbar-nav .dropdown>.dropdown-menu.dropdown-menu__rankings, .navbar-default .navbar-collapse .navbar-nav .dropdown.open>a, .navbar-custom .navbar-collapse .navbar-nav .dropdown.open>a {
    background-color: #111;
    border-color: #222;
}
.search-container:hover>.search-input input.search-bar, .search-container > .search-input input.search-bar, .search-icon-btn {
    background: #111;
}
.navbar-default .navbar-collapse .navbar-nav, .navbar-custom .navbar-collapse .navbar-nav, iframe#instagram-embed-0 {
    border-color: #222 !important;
}
.search-container:hover>.search-input input.search-bar, .search-container > .search-input input.search-bar, .search-icon-btn {
    border-color: #333;
}
hr, .Footer {
    border-color: #111;
}
.navbar-default .navbar-collapse .navbar-nav .dropdown.open>a, .navbar-custom .navbar-collapse .navbar-nav .dropdown.open>a {
    border-bottom-color: #111;
}
h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6, body, .container-post .post-date, .container-quiz .post-date, .container-wywiad .post-date, b, strong, .navbar-default .navbar-collapse .navbar-nav .dropdown>a .caret, .navbar-custom .navbar-collapse .navbar-nav .dropdown>a .caret, .polecane-column .posts-popular .post-popular__text>h4 a, .Caption, a.CaptionCommentsExpand, .container-quiz .column-quiz .form-group .radio>label {
    color: #eee;
}
.article-title > a, .navbar-default .navbar-collapse .navbar-nav.navbar-sub>li a, .navbar-custom .navbar-collapse .navbar-nav.navbar-sub>li a, .navbar-default .navbar-nav>li>a, .search-container:hover>.search-input input.search-bar, .search-container > .search-input input.search-bar, .search-icon-btn, a.Location, .thumbnail__top-10.thumbnail > .caption > h3 > span > [href], .btn-wrapper .btn-custom, .btn-wrapper .btn-custom-link {
    color: #eee !important;
}
 .thumbnail__top-10.thumbnail > .caption > h3 > span > [href] {
    color: #000 !important;
}
.Caption a {
    color: #005eff;
}
.container-post .redaktor-row .redakcja-icons>li>a .social-inactive, .container-quiz .redaktor-row .redakcja-icons>li>a .social-inactive, .container-wywiad .redaktor-row .redakcja-icons>li>a .social-inactive {
    -webkit-filter: grayscale(0%);
    filter: grayscale(0%);
    filter: contrast(200%) !important;
}
.thumbnail {
    background-color: #222;
}
.thumbnail.thumbnail__top-10 .caption h3>span {
    color: #eee !important;
    background-color: #000;
    box-shadow: 10px 0 0 #000, -10px 0 0 #000;
}
.thumbnail__top-10.thumbnail > .caption > h3 > span > [href] {
    color: #eee !important;
}
.container-quiz .column-quiz .form-group .radio>label {
	border-color: #333;
}
.container-quiz .column-quiz .form-group .radio input+label>i {
	filter: invert(100);
}
}
