css:
/* i really want this to be global */
* {
    background: rgba(255, 255, 255, .15);
}
.px-3 {
    background: #bbbbbb;
}
.dcg-header {
    border-bottom:3px solid #bbbbbb !important;
    color: #000000 !important; background: #d5d5d5 !important;
}
.xterm-viewport, .xterm-screen {
    background: #979797 !important;
}
.cm-keyword, div.CodeMirror span.CodeMirror-matchingbracket {
    font-weight: bolder !important; color: #00ff00;
}
div.CodeMirror span.CodeMirror-nonmatchingbracket, .cm-error {
    font-weight: bolder !important; color: #ff0000 !important;
}
.cm-def {
    color: #770000 !important;
}
.CodeMirror-line::selection, .CodeMirror-line > span::selection, .CodeMirror-line > span > span::selection, .cm-searching {
    background: rgba(0, 127, 127, .3) !important;
}
* {
    color: #777777; border: none; 
}
.cm-atom {
    color: #bb6600 !important;
}
.CodeMirror-cursor {
    border-left: 1px solid #ff3333 !important;
}
.cm-variable, .cm-variable-2, .cm-variable-3, .cm-property, .cm-qualifier {
    color: #777777 !important;
}
div.CodeMirror span.CodeMirror-matchingbracket {
    color: #ff00ff !important;
}
div.CodeMirror span.CodeMirror-nonmatchingbracket, .cm-error {
    color: #00ffff !important;
}
