css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("avaxhome.ws") {

center {
display:none;
}
div[align="left"]{
display:none;
}
.banner{
display: none;
border: 0px;
}
.container{
overflow:auto;
}
.news[author_id]{
display:block;
float:none;
width:100%;
height:100%;
}
.news{
float: left;
width:170px;
height: 300px;
overflow: hidden;
border: 1px solid black;
position:relative;
padding:15px;
}
.news .title, .info, .text{ font-size: 9px !important;}
.news .actions{
position:absolute;
top: 0px;
}
.news[author_id] .title, .info, .text{ font-size: 12px;}

.news .actions a{
width: 170px;
height: 300px;
display: block ;
}

.news img{
width: 140px;
}
.pagination{
position:absolute;
top: 15px;
}
.hr{
display:none;
}
#main-info-container > br{display:none;}
}
