@namespace url(http://www.w3.org/1999/xhtml);

/* Created by The Ev because he does not approve of advertisements being shoved down his throat without his permission. */

@-moz-document domain("interfacelife.com"){}

/* Fixes the wallpaper section */

@-moz-document url-prefix("http://interfacelift.com/wallpaper") {

/* Removes Padding ads */
#left, .padding>*:not(h2):not(p), #pagenums>div:first-child, #bottomleaderboard {display: none !important}

/* Centers remaining column, more or less */
#right {left: 0px !important;}
#main {width: 750px !important;}

}
