/* AGENT_SHEET */
/* Latest version is here: http://www.lekanger.no/lastfm/lastfm.css*/
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("last.fm") {

body
{
background-image:none !important;
background-color:white !important;

}
div,a,li,ul,.body,tbody,p
{
color:black !important;
}
.userPlays *
{ 
background-image:none !important;

}
.userPlays span
{
background-color:black !important;
width:16px !Important;
}
input,input *
{
font-weight:bolder !important;
}

hr
{
border:0.4px solid black !important;


}
.lfmBlack #header,
.lfmBlack #headerWrapper {
    background-image: none !important;
    background-color:lightyellow;
}
.last .transparent_png

{
display:none !important;
}
tr:hover,.footerStuff,#footerStuff,#uploadYourMusic,#footer_content,[name*="bgcolor"],#idBadgerDropDown *,.userType  *,table.chart tbody tr:hover td,
table.chart tbody tr.open td
{

    background-color:lightyellow !important;
    background-image:none !important;

}
}
