@-moz-document url-prefix("https://www.tumblr.com/") {
:root {
    --font-family: "Helvetica Neue", "HelveticaNeue", Helvetica, Arial, sans-serif;
}

/* -- COMMON ---------- */

/* sidebar blog title */ ._27qMJ ._6npwC .YDOFk,
/* sidebar dropdown gray text (logout, +New, etc.) */ ._1bWnc, ._2o396,
/* sidebar section numbers */ ._27qMJ ._3uGZX,
/* post header */ .L5OmD,
/* post header reblogged url */ ._1vRpg,
/* post reblogged header */ ._2zTTs,
/* post ask asker */ ._1eGuT ._2Awh5 ._1GpDQ, ._1eGuT ._2Awh5 ._2Y_1u,
/* post tags */ .pOoZl, .pOoZl .L0N4W,
/* post tags text (e.g. pinned post) */ .pOoZl ._nUEo,
/* post source */ .hjr__,
/* post footer */._2dGhQ, 
/* post footer note count */ ._3t3fM,
/* post notes */.tNaxc, 
/* post notes info */ ._3alr4, .BRURa ._3Cbi3,
/* post note tags */ ._3a9Me, ._3IfFb,
/* user info card content */ ._1AcPw._2myj6,
/* user info card follow button */ ._1paS- {
    font-size: 13px !important;
}

/* sidebar */ ._27qMJ,
/* sidebar dropdown */ ._2U-M4 ._1dLVj .XcYVy,
/* sidebar dropdown "change palette/new!" */ ._1Ts5C, ._3h1w4,
/* post content */ ._2m1qj,
/* post footer buttons */ ._33VXm, 
/* post notes note count */ ._2C7HC .Cs_JH .vtX8i,
/* post notes content */ ._3IfFb,
/* post notes reply input */ ._2TSny ._3e-z3, ._2TSny ._3GSAR,
/* user info card username */ ._3WA3M .onnEk ._1PyOv,
/* user info card action buttons */ ._2LUdG ._2qdYK, ._3hRvh,
/* blog description */ ._1AcPw {
    font-size: 14px !important;
}

/* sidebar */ ._27qMJ,
/* sidebar dropdown */ ._2U-M4 ._1dLVj .XcYVy,
/* sidebar dropdown gray text (logout, +New, etc.) */ ._1bWnc, ._2o396,
/* sidebar dropdown "change palette/new!" */ ._1Ts5C, ._3h1w4,
/* sidebar section numbers */ ._27qMJ ._3uGZX,
/* sidebar "explore all of tumblr" */ .SVPap,
/* post header */ .L5OmD,
/* post header reblogged url */ ._1vRpg,
/* post content */ ._2m1qj,
/* post ask asker */ ._1eGuT ._2Awh5 ._1GpDQ, ._1eGuT ._2Awh5 ._2Y_1u,
/* post reblogged header */ ._2zTTs,
/* post reblogged header follow button */ ._2eajK,
/* post tags */ .pOoZl, .pOoZl .L0N4W,
/* post tags text (e.g. pinned post) */ .pOoZl ._nUEo,
/* post source */ .hjr__,
/* post footer */ ._2dGhQ,
/* post footer buttons */ ._33VXm, 
/* post footer note count */ ._3t3fM,
/* post notes */.tNaxc,
/* post notes note count */ ._2C7HC .Cs_JH .vtX8i,
/* post notes info */ ._3alr4, .BRURa ._3Cbi3,
/* post notes content */ ._3IfFb,
/* post note tags */ ._3a9Me, ._3IfFb,
/* user info card username */ ._3WA3M .onnEk ._1PyOv,
/* user info card content */ ._1AcPw._2myj6,
/* user info card action buttons */ ._2LUdG ._2qdYK, ._3hRvh,
/* blog description */ ._1AcPw {
    line-height: 1.5;
}

/* -- SIDEBAR ---------- */

/* sidebar */
._27qMJ {
    font-weight: 700;
}
/* sidebar dropdown menu */
._1N6Lo ._1dLVj {
    min-height: 36px;
}
/* sidebar dropdown icons */
._2U-M4 ._2abFB svg {
    width: 16px;
    height: 16px;
}
/* sidebar dropdown blog title */
.-as-- ._26ExL ._2FxMi .aI5Fi .gAJ39 {
    font-size: 13px;
}
/* sidebar blog title */
._27qMJ ._6npwC .YDOFk {
    font-weight: 400;
}
/* sidebar section */
._27qMJ li {
    border-top: 1px solid hsla(0,0%,100%,.07);
}
._27qMJ li a {
    min-height: 26px;
}
/* sidebar radar/rec blogs */
._2biQz {
    font-size: 12px;
    text-transform: uppercase;
}
/* sidebar "explore all of tumblr" */
.SVPap {
    font-size: 12px;
}

/* -- POST ------------- */

/* post link style */
._2m1qj a {
    text-decoration: none;
    background-image: linear-gradient(180deg, rgba(var(--rgb-black), 0) 50%, rgba(var(--rgb-black), 0.15) 0);
    background-repeat: repeat-x;
    background-size: 1em 2px;
    background-position: 0 1.15em;
    padding-bottom: 2px;
}
/* post tagged user */
._3s7wL {
    text-decoration: none;
}
/* post reblogged header follow button */ ._2eajK {
    font-size: 11px !important;
}
/* post ask asker */
._1eGuT ._2Awh5 ._2Y_1u {
    margin-bottom: 0;
}
/* post blockquote */
._2m1qj._1pexr {
    margin-top: 20px;
    margin-bottom: 20px;
    padding-top: 0;
    padding-bottom: 0;
}
/* post tag */
.pOoZl .L0N4W:hover {
    text-decoration: none;
    opacity: .7;
}
/* post footer button icon */
._33VXm svg {
    width: 16px;
    height: 16px;
}
._36wTL svg {
    width: 14px;
    height: 13px;
}
/* post note reblog icon */
._2wmqU ._2KSuC ._3pSHT svg {
    width: 14px;
    height: 11px;
}
/* post note tags tracker icon */
.tNaxc .Hi_aW svg {
    width: 16px;
    height: 16px;
}
/* post note reply input */
._2TSny ._3e-z3, ._2TSny ._3xYIs {
    min-height: 40px;
}

/* -- XKIT ------------- */

/* timestamp */
.xkit--react .xtimestamp, .xkit--react .xtimestamp.xtimestamp-reblog {
    font-size: 11px !important;
}
/* one-click postage */
.xkit--react #x1cpostage_reblog i, .xkit--react #x1cpostage_queue i, .xkit--react #x1cpostage_draft i {
    width: 18px;
    height: 18px;
}
}
