css:
@-moz-document domain("hncgkmhphmncjohllpoleelnibpmccpj") {
	/*亮度★★★ ★★★ ★★ ★ ★ ★ ★ ★ ★ ★ ★ ★ ★ ★ ★ ★★ ★ ★ ★ ★ ★ */
	html,
	body {
		background: rgb(48, 48, 53)!important;
		-webkit-filter: brightness(89%)!important;
		filter: brightness(89%)!important;
		overflow: hidden!important;
		/*亮度★★★ ★★★ ★★ ★ ★ ★ ★ ★ ★ ★ ★ ★ ★ ★ ★ ★★ ★ ★ ★ ★ ★ */
	}
	/*主题字体颜色★★★ ★★★ ★★ ★ ★ ★ ★ ★ ★ ★ ★ ★ ★ ★ ★ ★★ ★ ★ ★ ★ ★*/
	.cm-s-default .cm-variable-2 {
		color: #e0e;
	}
	.cm-s-default .cm-tag {
		color: #9effff;
	}
	.cm-s-default span.cm-string {
		color: #3ad900;
	}
	.cm-s-default span.cm-atom {
		color: #f133f1;
	}
	.cm-s-default .cm-keyword {
		color: #ffee80;
	}
	.cm-s-default .cm-number {
		color: #ffd0d0;
	}
	.cm-s-default .cm-meta,
	.cm-s-default .cm-qualifier {
		color: #ccc;
	}
	.cm-s-default .cm-comment {
		color: #77f;
	}
	div.CodeMirror span.CodeMirror-matchingbracket {
		color: #0f0;
	}
	a.style-name,
	span.CodeMirror-matchingbracket {
		color: #00ff86!important;
	}
	.cm-s-default .cm-builtin {
		color: rgb(238, 170, 170);
	}
	.cm-s-default .cm-type,
	.cm-s-default .cm-variable-3,
	.cm-s-erlang-dark span.cm-type,
	.cm-s-erlang-dark span.cm-variable-3 {
		color: #f5629f;
	}
	.cm-s-erlang-dark .CodeMirror-matchingbracket,
	div.CodeMirror span.CodeMirror-matchingbracket {
		outline: rgba(221, 221, 221, 0) solid 1px;
		color: #d5ff00!important;
		background: rgba(192, 192, 192, 0.5)!important;
		/*主题字体颜色★★★ ★★★ ★★ ★ ★ ★ ★ ★ ★ ★ ★ ★ ★ ★ ★ ★★ ★ ★ ★ ★ ★*/
	}
	/*代码窗口*/
	.CodeMirror-wrap {
		border: none!important;
		min-height: 850px!important;
		max-height: 890px!important;
	}
	/*滚动条*/
	::-webkit-scrollbar {
		width: 10px!important/*垂直滚动条的宽*/
		;
		height: 10px!important/*水平滚动条的高*/
		;
		background-color: rgba(13, 173, 253, 0)!important;
	}
	::-webkit-scrollbar-thumb {
		background-color: rgb(31, 106, 234)!important;
		border-radius: 50px!important;
	}
	::-webkit-scrollbar-thumb:hover {
		background-color: rgb(24, 90, 202)!important;
	}
	::-webkit-scrollbar-button {
		display: none!important;
	}
	.mdl-card__supporting-text {
		padding: 0px!important;
	}
	/*::-webkit-scrollbar — 整个滚动条.
::-webkit-scrollbar-button — 滚动条上的按钮 (上下箭头).
::-webkit-scrollbar-thumb — 滚动条上的滚动滑块.
::-webkit-scrollbar-track — 滚动条轨道.
::-webkit-scrollbar-track-piece — 滚动条没有滑块的轨道部分.
::-webkit-scrollbar-corner — 当同时有垂直滚动条和水平滚动条时交汇的部分.
::-webkit-resizer — 某些元素的corner部分的部分样式(例:textarea的可拖动按钮).*/
	html,
	a,
	p,
	span,
	pre,
	em,
	label,
	button {
		font-family: "Microsoft YaHei" sans-serif!important;
		font-size: 24px!important;
		font-weight: bold!important;
	}
	#cloud_filelist > tr > td.name.mdl-data-table__cell--non-numeric,
	#cloud_filelist > tr > td.size {
		font-size: 20px!important;
		font-weight: bold!important;
	}
	#xstyle-manage > div.mdl-layout__container > div > header > div > div.mdl-layout__tab-bar.mdl-js-ripple-effect.is-casting-shadow.mdl-js-ripple-effect--ignore-events > a:hover {
		color: #FE4081!important;
		transition: all 0.5s!important;
	}
	/*代码窗口颜色*/
	#main-content > div.left-section > div.mdl-card.mdl-shadow--2dp.code-area > div > div > div.CodeMirror-scroll,
	#main-content > div.left-section > div.mdl-card.mdl-shadow--2dp.code-area > div > div > div.CodeMirror-scroll > div.CodeMirror-gutters {
		background: #042e51!important;
	}
	#main-content > div.left-section > div.mdl-card.mdl-shadow--2dp.code-area > div > div > div.CodeMirror-scroll > div.CodeMirror-sizer > div > div > div > div.CodeMirror-code > div.CodeMirror-activeline > pre {
		background: rgba(16, 87, 213, 0.25)!important;
	}
	/*窗口颜色*/
	html.mdl-js,
	.mdl-layout__content,
	.left-section,
	div.mdl-car.mdl-car--expand,
	.code-area,
	.right-section,
	.option,
	.file,
	.cloud,
	table,
	#cloud_filelist > tr,
	#cloud_loaded:hover,
	#installed {
		background: rgb(48, 48, 53)!important;
		border: none!important;
		box-shadow: none!important;
	}
	/*行号右边的边框*/
	.CodeMirror-gutters {
		border-right: 2px solid #626262!important;
	}
	.CodeMirror-lint-markers {
		width: 12px!important;
	}
	/*样式列表*/
	#installed > section {
		width: calc(50% - 20px)!important;
		background: rgb(85, 85, 85)!important;
		border-radius: 10px!important;
		box-shadow: none!important;
		margin-bottom: 15px!important;
		margin-left: 12px!important;
	}
	#sort-options {
		margin-left: 12px!important;
	}
	#installed .mdl-list__item {
		width: calc(100% - 15px)!important;
		background: rgb(85, 85, 85)!important;
		border-radius: 10px!important;
		margin-bottom: 15px!important;
		margin-left: 7px!important;
		transition: all .25s!important;
	}
	#installed .mdl-list__item:hover,
	#installed > section:hover {
		background: rgb(77, 77, 77)!important;
		transition: all .25s!important;
	}
	code,
	#cloud_filelist > tr:hover {
		background: rgb(68, 68, 68)!important;
	}
	#disable-all-container,
	#header > div > nav > div.mdl-menu__container.is-upgraded.is-visible > div {
		background: #3a3a3a!important;
		border: none!important;
	}
	#writeNewMenu > li,
	#searchStylesMenu > li {
		color: rgb(196, 196, 196)!important;
	}
	#writeNewMenu > li:hover,
	#searchStylesMenu > li:hover {
		background: #8f8f8f!important;
	}
	.mdl-switch.enable-switcher.is-checked.is-upgraded > .mdl-switch__thumb,
	.mdl-switch.is-checked.is-upgraded > .mdl-switch__thumb {
		width: 15px!important;
		height: 15px!important;
		position: absolute!important;
		top: 7px!important;
		left: 18px!important;
		background-color: rgb(40, 41, 42)!important;
		transition: 1.5s!important;
	}
	.mdl-switch.enable-switcher.is-checked.is-upgraded > .mdl-switch__track,
	.mdl-switch.is-checked.is-upgraded > .mdl-switch__track {
		background-color: rgb(137, 215, 25)!important;
		height: 20px!important;
		transition: 1.5s!important;
	}
	.mdl-switch.enable-switcher.is-upgraded > .mdl-switch__thumb,
	.mdl-switch.is-upgraded > .mdl-switch__thumb {
		background-color: rgb(40, 41, 42)!important;
		width: 15px!important;
		height: 15px!important;
		position: absolute!important;
		top: 7px!important;
		left: 3px!important;
		transition: 1.5s!important;
	}
	.mdl-switch.enable-switcher.is-upgraded > .mdl-switch__track,
	.mdl-switch.is-upgraded > .mdl-switch__track {
		background-color: rgb(124, 128, 128)!important;
		height: 20px!important;
		transition: 1.5s!important;
	}
	span.CodeMirror-matchingbracket,
	a.style-name {
		color: #89d719!important
	}
	.close,
	#help-popup {
		display: none!important;
	}
	.style-edit-link,
	.delete,
	.style-export-link,
	.mdl-button.mdl-button--colored,
	#installed > section > div.mdl-card__actions.mdl-card--border > a.mdl-button.mdl-button--colored.mdl-js-button.mdl-js-ripple-effect.update > span {
		color: rgb(255, 197, 36)!important;
	}
	.right-section span,
	.right-section input,
	.right-section button,
	i,
	div,
	.mdl-js-button span,
	#zerostyles,
	.mdl-card__title.mdl-card--expand > h2,
	.mdl-card.mdl-shadow--2dp.file > div.mdl-card__supporting-text.backup-buttons > a,
	#unavailable,
	span.style-name {
		color: rgb(209, 209, 209)!important;
	}
	.mdl-checkbox__box-outline,
	.mdl-radio__outer-circle {
		border-color: rgb(219, 219, 219)!important;
		border-radius: 6px!important;
	}
	.mdl-radio__inner-circle {
		width: 12px!important;
		height: 12px!important;
		background-color: #ff7c00!important;
		position: absolute!important;
		z-index: 1!important;
		top: 6px!important;
		left: 2px!important;
		border-radius: 20%;
	}
	.right-section label {
		color: rgb(176, 161, 255)!important;
	}
	.mdl-textfield__input {
		border: none;
		border-bottom: 2px solid rgba(175, 175, 175, 0.5);
	}
	#cloud_export,
	#cloud_reload_list,
	#cloud_filelist > tr > td > button.mdl-button.mdl-js-ripple-effect.import,
	#file-all-styles,
	#unfile-all-styles,
	#backup-panel > div > div.mdl-card.mdl-shadow--2dp.file > div.mdl-card__supporting-text.backup-buttons > a,
	#cloud_load_list,
	#sort-options,
	#update-all-styles,
	#style-panel > div > div.action > a,
	#install-from-file {
		background: #3F51B5!important;
		color: rgb(255, 255, 255)!important;
	}
	#sort-options,
	#update-all-styles,
	#style-panel > div > div.action > a,
	#install-from-file,
	#file-all-styles,
	#unfile-all-styles,
	#backup-panel > div > div.mdl-card.mdl-shadow--2dp.file > div.mdl-card__supporting-text.backup-buttons > a,
	#cloud_load_list,
	#cloud_export,
	#cloud_reload_list,
	#cloud_filelist > tr > td > button.mdl-button.mdl-js-ripple-effect.import {
		border-radius: 7px!important;
	}
	#cloud_export:hover,
	#cloud_reload_list:hover,
	#cloud_filelist > tr > td > button.mdl-button.mdl-js-ripple-effect.import:hover,
	#file-all-styles:hover,
	#unfile-all-styles:hover,
	#backup-panel > div > div.mdl-card.mdl-shadow--2dp.file > div.mdl-card__supporting-text.backup-buttons > a:hover,
	#cloud_load_list:hover,
	#sort-options:hover,
	#update-all-styles:hover,
	#style-panel > div > div.action > a:hover,
	#install-from-file:hover {
		background: #b54a3f!important;
		color: rgb(255, 255, 255)!important;
		transition: all 0.5s!important;
	}
	.mdl-checkbox.is-checked .mdl-checkbox__tick-outline {
		background: rgb(255, 255, 255) 0!important;
	}
	#cloud_filelist > tr > td > button.mdl-button.mdl-js-ripple-effect.delete:hover {
		background: transparent!important;
		color: red!important;
	}
	#save-link,
	#beautify/*保存，美化*/
	,
	#gt_connect/*连接*/
		{
		border-radius: 15px!important;
		background-image: linear-gradient(90deg, rgb(107, 75, 181) 0, rgb(196, 48, 211));
		transition: all 0.5s!important;
	}
	#save-link:hover,
	#beautify:hover/*保存，美化*/
	,
	#gt_connect:hover/*连接*/
		{
		background-image: linear-gradient(90deg, rgb(134, 96, 224) 0, rgb(206, 72, 219));
		transition: all 5s!important;
	}
	#backup-panel > div > div.mdl-card.mdl-shadow--2dp.cloud > div.mdl-card__supporting-text > table > thead > tr > th {
		color: #00ff86!important;
		font-size: 20px!important;
	}
	#main-content > div.right-section.mdl-card.mdl-shadow--2dp > div {
		position: relative!important;
		right: -10px!important;
	}
	.mdl-textfield.mdl-textfield--floating-label.style-update-url-box.is-upgraded > label,
	#style-panel > div.mdl-textfield.mdl-textfield--floating-label.style-name-box.is-dirty.is-upgraded > label,
	#tabSize-label,
	#fontSize-label,
	#editor-panel > div:nth-child(10) {
		font-size: 19px!important;
		font-weight: bold!important;
	}
	input#name {
		font-size: 25px!important;
		font-weight: bold!important;
	}
	#back-button > i:hover {
		width: 18px!important;
		height: 18px!important;
		color: red!important;
		transition: all 0.5s!important;
	}
}
