css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("netvibes.com") {

/* Modules */
.module, .moduleFrame, .moduleHeaderContainer, .moduleHeader {-moz-border-radius: 0 8px !important;}
.resizable .module, .resizable .moduleEdit {margin: 1px !important; margin-bottom: 12px !important;}

/* Notification */
#notification-box {display: none !important;}

/* Tabs */
#divTabs ul li {padding: 0px 8px 1px 6px !important; border-bottom: 2px solid #DDDDDD !important;}
#divTabs ul li:hover {border-bottom: 2px solid #FFA904 !important;}
#divTabs ul li.selected {border-bottom: 2px solid #FFA904 !important;}
.innerTab {-moz-border-radius: 0 8px !important;}

/* Unread */
.innerTab .unread {display: none !important;}

/* Footer */
#footer {display: none !important;}

/* share */
.share {display: none !important;}

}
