@-moz-document domain("codebeautify.org") {
 
::-webkit-scrollbar
 {
 	/* Width */
 	width: 1.2em!important;
 }

 /* Color of handle ___________________________________________________________________________*/
 ::-webkit-scrollbar-thumb
 {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover
 {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track ______________________________________________________________________________*/
 ::-webkit-scrollbar-track-piece,
 ::-webkit-scrollbar-track
 {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom ___________________________________________________________*/
 ::-webkit-scrollbar-button
 {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover
 {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of the bottom corner of the scrollable element, where two scrollbar meet. ______________*/
 ::-webkit-scrollbar-corner
 {
 	background: #530205!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-corner:hover
 {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }
}

@-moz-document domain("codebeautify.org") {
/*__________________________________________________________________________________*/
*
{
	text-decoration: none!important;
}

selector
{
	text-transform: capitalize!important;
}

.XpathExampleHead,
.form-wrapper,
.buttons1,
.dataTables_wrapper .dataTables_paginate .paginate_button.current,
.dataTables_wrapper .dataTables_paginate .paginate_button.current:hover,
.ui-widget-content,
.ui-widget-header,
:focus,
[style="border: 1px dotted white;padding: 5px;text-align: center;color: white;"],
select
{
	border: none!important;
	outline: none!important;
	box-shadow: none!important;
}

table {
    border-collapse: separate;
    border-spacing: 0 16px;
}

/*___ Fonts ________________________________________________________________________*/
.footersub1 h4
{
	font-size: 21px!important;
}

.ace_editor,
body,
code
{
	font-size: 20px!important;
}

.footersub1 li a,
.uneditable-input,
input[type=color],
input[type=date],
input[type=datetime-local],
input[type=datetime],
input[type=email],
input[type=month],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
input[type=time],
input[type=url],
input[type=week],
select,
textarea
{
	font-size: 19px!important;
}

.success,
.span12.marginleft0.randomnamePicker h5
{
	font-size: 17px!important;
}

.uneditable-input,
input[type=color],
input[type=date],
input[type=datetime-local],
input[type=datetime],
input[type=email],
input[type=month],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
input[type=time],
input[type=url],
input[type=week],
select,
textarea
{
	line-height: 36px!important;
}

.footersub1 li a,
body
{
	line-height: 23px!important;
}

/*___ Remove Items _________________________________________________________________*/
selector
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/
selector
{
	top: 10px!important;
}

/*___ Margins ______________________________________________________________________*/



.label-parent-file,
.article .cnt
{
	margin: 12px!important;
}

.xpathLeftContainer
{
	margin: 12px 6px!important;
}

.infoFeatureDiv li
{
	margin: 12px 6px!important;
}

/*___ Paddings _____________________________________________________________________*/

.xpathLeftContainer,
.viewerEditor,
.xmltree
{
	padding: 12px!important;
}

.xpathLeftContainer tr
{
	padding: 3px 6px!important;
}

table.dataTable tbody td
{
	padding: 16px 10px;
}

/*___ Heights ______________________________________________________________________*/
selector
{
	max-height: none!important;
}

selector
{
	height: auto!important;
}

/*___ Widths _______________________________________________________________________*/

body
{
	max-width: none!important;
}

selector
{
	width: auto!important;
}

.actionButtonDiv,
.row-fluid .span12,
.validatorContainer
{
	width: 98%!important;
}

.row-fluid .span9
{
	width: 73%;
}

.xpathLeftContainer {
    width: 47%;
}
}

@-moz-document domain("codebeautify.org") {
/*__________________________________________________________________________________*/
a[href],
td span
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.ace-tm .ace_gutter-active-line,
.blink,
.dataTables_wrapper .dataTables_paginate .paginate_button.current,
.dataTables_wrapper .dataTables_paginate .paginate_button.current:hover,
.footersub1 li a:hover,
a[href]:hover span,
a[href]:hover,
h1,
input[type="checkbox"]:checked + label,
input[type="radio"]:checked + label,
tbody td:first-child
{
	color: #ff0!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.success,
.xpathLeftContainer,
.ace_gutter-cell,
.ace_line,
.dataTables_wrapper .dataTables_filter,
.dataTables_wrapper .dataTables_info,
.dataTables_wrapper .dataTables_length,
.dataTables_wrapper .dataTables_paginate .paginate_button,
.dataTables_wrapper .dataTables_paginate,
.dataTables_wrapper .dataTables_processing,
.infoFeatureDiv,
.jsoneditor-statusbar > .jsoneditor-curserinfo-val,
.resultIframe,
.row-fluid [class*=span]:first-child,
.span12.marginleft0.randomnamePicker h5 span,
.ui-widget-content,
.xmltree .LIText,
.xmltree .currSel li .LIText,
.xmltree .noKids .LIText:hover,
li,
p,
td,
table.dataTable td,
table.dataTable.display tbody tr.even > .sorting_1,
table.dataTable.display tbody tr.odd > .sorting_1,
table.dataTable.order-column.stripe tbody tr.even > .sorting_1,
table.dataTable.order-column.stripe tbody tr.odd > .sorting_1
{
	color: #fff!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Khaki ________________________________________________________________________*/

legend,
.mainLeftInputDiv1,
.ui-dialog .ui-dialog-title,
h1,
h2,
table.dataTable thead th,
table.dataTable tfoot th,
th,
u
{
	color: #F0E890!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Burlywood ____________________________________________________________________*/

.creditCardinnerLeftDiv,
.fName,
[role="row"] td:nth-child(5),
b,
h3,
h4,
strong
{
	color: #e0b880!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Sandy Brown __________________________________________________________________*/

div.jsoneditor-statusbar > .jsoneditor-curserinfo-label,
h5,
h6,
lable
{
	color: #F0A060!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Blue _________________________________________________________________________*/

em,
time
{
	color: #07beff!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.row-fluid select[class*=span],
input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
option,
option:checked,
select,
textarea
{
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #f6e6e6!important;
	padding-left: 18px!important;
}

/*__________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f;
	opacity: 1;
}

/*___ Misc _________________________________________________________________________*/
.ace-tm .ace_constant.ace_numeric
{
	color: rgb(202, 205, 0);
}

.ace-tm .ace_storage,
.ace-tm .ace_keyword
{
	color: #00ffad;
}

.ace-tm .ace_variable
{
	color: rgb(255, 211, 86);
}

.ace-tm .ace_constant
{
	color: rgb(255, 18, 24);
}

.ace-tm .ace_string
{
	color: rgb(11, 255, 20);
}

.ace-tm .ace_comment.ace_doc,
.ace-tm .ace_comment
{
	color: rgb(143, 145, 144);
}

.ace_gutter-cell,
.ace_tooltip,
.ace-tm .ace_cursor
{
	color: #f5f5f5;
}

.ace-tm .ace_support.ace_type,
.ace-tm .ace_support.ace_class
{
	color: rgb(166, 175, 255);
}

.ace-tm .ace_support.ace_function
{
	color: rgb(255, 130, 251);
}

.ace-tm .ace_constant.ace_language
{
	color: rgb(125, 129, 255);
}

.ace-tm .ace_keyword.ace_operator
{
	color: rgb(255, 238, 200);
}

.ace-tm .ace_entity.ace_name.ace_function
{
	color: #00adff;
}

.ace-tm .ace_support.ace_constant
{
	color: rgb(255, 162, 23);
}

.ace-tm .ace_meta.ace_tag
{
	color: rgb(2, 162, 255);
}

.ace-tm,
.infoDiv,
.jsoneditor-popover,
.jsoneditor-schema-error,
code,
div.jsoneditor td,
div.jsoneditor textarea,
div.jsoneditor th,
div.jsoneditor-field,
div.jsoneditor-value,
pre.jsoneditor-preview
{
	color: #f5f5f5!important;
}

.xmltree .attr span
{
	color: #6cf76c;
}

.ace-jsoneditor .ace_variable
{
	color: #fff80f;
}

div.jsoneditor-readonly
{
	color: #ff7777;
}

div.jsoneditor-value.jsoneditor-string
{
	color: #00ff00;
}
}

@-moz-document domain("codebeautify.org") {
/*__________________________________________________________________________________*/
body,
html
{
	background: url(https://image.ibb.co/hVmiOe/wallpaper_with_black_background_45_free_wallpaper.jpg)!important;
	background-attachment: fixed!important;
}

selector
{
	background-image: none;
}

.ace-tm .ace_marker-layer .ace_active-line
{
	background: rgba(255, 2, 2, 0.08);
}

.even,
.odd
{
	background: #000!important;
}

#excelEditor .resultIframe table
{
	background: #e8e7e7!important;
}

.ace-tm .ace_marker-layer .ace_selection
{
	background: rgb(40, 47, 56);
}

/*___ Background Gray ______________________________________________________________*/

.ace-tm,
.footerpart,
.jsoneditor-statusbar,
.navbar-inner,
div.jsoneditor-menu
{
	background-color: #060505!important;
}

.ace-jsoneditor .ace_gutter,
.ace-tm .ace_gutter,
.jsoneditor-navigation-bar,
.subTitleDiv
{
	background-color: #0a0909!important;
}

/*___ Background 0000 ______________________________________________________________*/

.XpathExampleHead,
.dataTables_wrapper .dataTables_paginate .paginate_button.current,
.dataTables_wrapper .dataTables_paginate .paginate_button.current:hover,
.fileDiffMainContainer,
.stringViewMainContainer,
.ui-widget-content,
.ui-widget-header,
.zebra-striped tbody tr:nth-child(odd) td,
.zebra-striped tbody tr:nth-child(odd) th
{
	background: rgba(0, 0, 0, 0)!important;
}

/*___ Background-color 0000 ________________________________________________________*/

.ace-jsoneditor .ace_scroller,
.ace-jsoneditor.ace_editor,
.back,
.dataTables_wrapper .dataTables_paginate .paginate_button.current,
.dataTables_wrapper .dataTables_paginate .paginate_button.current:hover,
.footerLinkDiv,
.footer_map,
.form-wrapper,
.jsoneditor-frame,
.jsoneditor-manual,
table.dataTable.display tbody tr.even > .sorting_1,
table.dataTable.display tbody tr.odd > .sorting_1,
table.dataTable.order-column.stripe tbody tr.even > .sorting_1,
table.dataTable.order-column.stripe tbody tr.odd > .sorting_1
{
	background-color: rgba(0, 0, 0, 0)!important;
}

/*__ Red __________________________________________________________________________*/

.fileup,
.btn,
.btn1,
.ui-widget .ui-widget:last-child
{
	background-image: linear-gradient(to bottom right, #f00, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.fileup:hover,
.btn1:hover,
.btn:hover,
.ui-widget .ui-widget:last-child:hover
{
	background-image: linear-gradient(to bottom right, #f11, #000)!important;
	color: #ff0!important;
}

.fileup:active,
.btn1:active,
.btn:active,
.ui-widget .ui-widget:last-child:active
{
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*___ Green ________________________________________________________________________*/

.ui-widget .ui-widget:first-child
{
	background-image: linear-gradient(to bottom right, #81BC2E, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.ui-widget .ui-widget:first-child:hover
{
	background-image: linear-gradient(to bottom right, #92cd3f, #000)!important;
	color: #ff0!important;
}

.ui-widget .ui-widget:first-child:active
{
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*___ Plain Black __________________________________________________________________*/

.xpathLeftContainer,
table.bordered-table,
#right-help,
.article .cnt,
.center,
.creditCardinnerLeftDiv,
.jsoneditor,
.row-fluid .span9,
.span12.sidebar,
.viewerEditor,
.xmltree
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}


.xpathLeftContainer:hover,
table.bordered-table:hover,
#right-help:hover,
.article .cnt:hover,
.center:hover,
.creditCardinnerLeftDiv:hover,
.jsoneditor:hover,
.row-fluid .span9:hover,
.span12.sidebar:hover,
.viewerEditor:hover,
.xmltree:hover
{
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*___ Plain ________________________________________________________________________*/

.ace_tooltip,
.label-parent-file,
.ui-widget.ui-widget-content
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

.ace_tooltip:hover,
.label-parent-file:hover,
.ui-widget.ui-widget-content:hover
{
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*__ Blue Green Gradient _____________________________________________________*/

.success
{
	background: linear-gradient(to bottom, #08b124, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

/*___ Highlight Background _________________________________________________________*/

.xpathLeftContainer tr:hover,
table.bordered-table tbody tr:hover,
.ace-jsoneditor .ace_gutter-active-line,
.ace-jsoneditor .ace_marker-layer .ace_active-line,
.ace-tm .ace_gutter-active-line,
.buttons1:hover,
.currentselected,
.liststyles li > span > a:HOVER
{
	background: linear-gradient(to right, #383838, #0000)!important;
	border-left-color: #434343!important;
}
}

