css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("feltet.dk") {

/* Remove ads */
*[id^="banner"],
#struct_topAds,
.box_160_default,
.box_300_default,
.box_468_default,
#cntdwn_left,
#cntdwn_right,
#struct_bottomAds,
#octo_body
{ display: none !important; }

/* Remove background ads */
body,
#octo_body
{ background-image: none !important; }

/* Remove deadspace from top */
#side, #tools, #tipenven
{ top: 0px !important; }

/* Move content a bit down */
#indhold
{ padding: 5px 10px !important; }

/* Remove Facebook */
*[href*="facebook"],
*[src*="facebook"]
{ display: none !important; }

}
