css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://userstyles.org/") , url-prefix("https://userstyles.org/") {

/* Edit Delete Icons */
ul#style-list a.edit-link, ul#style-list a.delete-link {font-size:0 !important; position:relative; top:2px; visibility:hidden;}
ul#style-list a.edit-link:before {content:url(data:image/png;base64,); visibility:visible;}
ul#style-list a.delete-link:before {content:url(data:image/png;base64,); visibility:visible;}
ul#style-list a.edit-link, ul#style-list a.delete-link {opacity:0.5;}
ul#style-list a.edit-link:hover, ul#style-list a.delete-link:hover {opacity:1;}
}
