@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tipsbladet.dk") {

/* Remove ads */
#FlashID1x,
*[class*="flash-hide"],
*[class*="banner"],
*[src*="newsticker"],
div[id*="emediate"],
embed[src*="emediate"],
div[class*="emediate"]
{ display: none !important; }

/* Center page */
#outer_cointainer
{ width: 950px !important; }
#container
{ float: none !important;
  margin: auto !important;
  width: 950px !important; }

/* Remove top */
*[class="top-links"]
{ display: none !important; }

/* Set background colors */
body
{ background-color: #414c5b !important;
  background-image: none !important; }

/* Remove gossip title */
.gosip-title
{ display: none !important; }


/* Re-position gossip header */
.node
{ padding: 0px 0 19px !important;}

/* Remove haandbold.com */
div[id*="block-aggregator-category-4"]
{ display: none !important; }

/* Remove tagadelic-crap */
div[id*="block-tagadelic"]
{ display: none !important; }

}
