css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://librivox.org/forum) {

body { font-size:1em !important; }
a:link, a:visited, a:hover, a:active, a:focus { color:#0047a3 !important; font-style:italic !important; text-decoration:none !important;}
a.forumlink { color:#060065 !important; font-size:1.2em !important; font-style:normal !important; text-decoration: none !important; }
a.cattitle { color:#060065 !important; font-style:normal !important; text-decoration: none !important;}
a.topictitle { color:#060065 !important; font-size:1.1em !important; font-style:normal !important; text-decoration: none !important; }
td.row1, td.row2, td.row3, td.row3right { background:#f9f9e8 !important; border:1px dotted #efefef!important; }

td.row1 > table, td.row2 > table { font-family:serif !important; font-weight: 600 !important; }

span.genmed { font-weight:bold !important; }
th { background: #8ebaff !important; }
a.mainmenu { font-style:normal !important; }

}
