css:
@-moz-document domain("www.nitrotype.com") {
.is-single-line .dash-copy {
    height: 150px!important;
    padding-top: 40px;
}
.dash {
    height: auto;
    padding-bottom: 90px;
}
.dash-center div[class="dash-copyContainer"] .dash-copy {
    transform: none!important;
}
.dash-actions {
    position: absolute;
    top: -20px;
    left: 250px;
}
.dash-copyContainer {
    margin-top: 15px;
}
.racev3-ui {
    margin-top: -274px;
}
canvas[width="1018"] {
    position: absolute; 
  overflow: hidden; 
  clip: rect(0, 0, 0, 0); 
  height: 1px; width: 1px; 
  margin: -1px; padding: 0; border: 0; 
}
.is-state-waiting .raceLight-status {
    left: -590px;
    right: -515px;
    top: -320px;
}
.is-state-three .raceLight-status {
    left: -620px;
    right: -515px;
    top: -370px;
    background: linear-gradient(to bottom, #a16f03, #f2b530)!important;
}
.is-state-two .raceLight-status {
    left: -620px;
    right: -515px;
    top: -417px;
}
.is-state-one .raceLight-status {
    left: -620px;
    right: -515px;
    top: -467px;
    background: linear-gradient(to bottom, #defa0a, #a9f525)!important;
}
.is-state-start .raceLight-status {
    left: -600px;
    right: -515px;
    top: -517px;
}
img.raceLight-tree, .raceChat, .dashShield, .dash-bg, .dash-pos, .dash-inv, .racev3Map-track, .raceLight-orbs, .racev3Map-pole, .nmDash-message, .raceChat-pickerToggle, .racev3Pre-tip {
    display: none!important;
}
.racePanel {
    position: absolute;
    top: 20px;
}
.racev3-track {
    background-color: rgba(0,0,0,0);
}
}
