css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.ubuntu.org.cn") {

/*
    20090703:
        add forum rules in some board
*/

    /* body */
    body.ltr {
        background: #444444 !important;
        color: #cccccc !important;
    }

    /* link */
    a {
        color: #DBBA75 !important;
    }
    a:visited {
        color: #9F4C09 !important;
    }

    /* global element */
    input,
    select,
    textarea
    {
        background: #444444 !important;
        color: #cccccc !important;
    }

    /* skills */
    p.skills {
        color: #99cc66 !important;
    }

    /* header */
    div#logodesc {
        background: #333333 !important;
    }
    div#nav1 a {
        color: #000000 !important;
    }

    /* table */
    table.tablebg {
        background: #9F4C09 !important;
    }

    /* cat */
    td.cat,
    td.catdiv
    {
        background: #000000 !important;
    }

    /* row */
    td.row1,
    td.row2
    {
        background: #333333 !important;

    }
    td.row3 {
        background: #222222 !important;
    }
    p.forumdesc {
        color: #cccccc !important;
    }

    /* post */
    tr.row1,
    tr.row2
    {
        background: #333333 !important;
    }
    td.spacer {
        background: #444444 !important;
    }
    p.moderators,
    td.nav,
    p.breadcrumbs
    {
        color: #cccccc !important;
    }
    div.codecontent {
        background: #444444 !important;
        color: #99cc66 !important;
    }
    div.quotetitle {
        color: #bbbbbb !important;
    }
    div.quotecontent {
        background: #444444 !important;
        color: #bbbbbb !important;
    }
    div.attachtitle {
        color: #bbbbbb !important;
    }
    div.attachcontent {
        background: #444444 !important;
        color: #bbbbbb !important;
    }

    /* footer */
    span.copyright {
        color: #aaaaaa !important;
    }
    span.nav {
        color: #bbbbbb !important;
    }

    /* forum rules in some board */
    div.forumrules {
        background: #7F5217 !important;
    }


}
