@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bt.dk") {

/* Set background color, center & resize */
body
{ background-position: center !important;
  background-repeat: repeat-y !important;
  background-image: url("data:image/png;base64,") !important;
  background-color: #d41c23 !important; } /* alt colors: #d41c23 #870505 #c8181e #c7181e */
.page.wrapper
{ width: 960px !important; }

/* Set article width */
.container_24 .prefix_4
{ padding-left: 0px !important;
  width: 620px !important; }

/* Remove ads */
*[class*="banner-"],
.jobzonen,
*[src*="banner"],
.article-side-banner,
iframe,
*[id^="adform-"]
{ display: none !important }

/* Remove most PLUS */
.annonce-content,
.footer-header,
.plus-overview
{ display: none !important }

/* Remove Facebook & other SS */
.facebook-box-wrapper,
.service-block-tools,
*[src*="facebook.com"],
a.tip-bt
{ display: none !important }

}
