@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mbank.eu") 
{
  ul.table li.alternate {background-color:#FFFFB4 !important} /*table every second row*/

  p.Actions  {padding:2px !important; border-top:1px dotted #CCC  !important} /*table action box*/

  ul.table p.Actions a  {text-decoration: none !important}    /*table action links*/

}
