css:
@-moz-document domain("jeja.pl") {
body {
    background: #000000 !important;
}

.movie-top {
    -webkit-box-shadow: 0 28px 11px 0 rgba(0, 0, 0, .83);
    -moz-box-shadow: 0 28px 11px 0 rgba(0, 0, 0, .83);
    box-shadow: 0 28px 11px 0 rgba(0, 0, 0, .83);
}

.dow-right-text-shadow {
    box-shadow: inset 0 -190px 115px -84px rgb(45, 45, 45);
    -moz-box-shadow: inset 0 -190px 115px -84px rgb(45, 45, 45);
    -webkit-box-shadow: inset 0 -190px 115px -84px rgb(45, 45, 45);
}

p,
span,
strong,
h3,
h2,
h1,
textarea,
label,
select,
option,
label::before,
.module-c li,
.footer-5 strong,
.profil-liczby-right div,
.list-badges li,
.box-1x1-text,
.text,
.profil-opis-text,
.profil-dane-right,
.nick,
.page-text,
.left-wrap,
.search::before,
.active-login,
.left-wrap div[style*="color: #212121;"],
.dow-left-text li{
    color: #777 !important;
}



footer::before,
#jeja_ifr,
#wrapper-wrap,
.movie-top,
.list-chat li.form::before {
    background: #111 !important;
}

textarea,
tags,
select,
input,
#search p,
#search h2,
#nav a,
#search,
#logo,
#root,
#content,
#top::before,
.list-c li,
.sort a,
.notifications li,
.rozwin-click strong,
.zwin-click strong,
.menu-right li,
.list-c::before,
.menu,
.box-autor,
.movie-s-box,
.game-s-box,
.pow-user:hover,
.select-down,
.best-month-box,
.module-b,
.game-su-box,
.occasional-s-box {
    background-color: #222 !important;
}

.cnt_votes_down,
.cnt_votes_up,
.input::before {
    color: #555 !important;
}

.box-autor-name {
    color: #666;
    font-weight: bold;
}



.list-d li,
.sort .active a,
.movie-active,
#nav .active a {
    background-color: #444 !important;
}

.cText p a[href^="https://www.jeja.pl/user,"] {
    font-weight: bold;
}


.movie-active:hover {
    background-color: #555 !important;
}

/* Ramki */
.info-1,
li.success {
    background-color: #051d06 !important;
}
.info-1 p,
li.success {
    color: #526f54 !important;
}

.info-2,
li.error {
    background-color: #220202 !important;
}
.info-2 p,
li.error {
    color: #7e6363 !important;
}

.info-3,
li.info {
    background-color: #0d1a2b !important;
}
.info-3 p,
li.info {
    color: #61748d !important;
}

/* Z jakiegoś powodu usuwanie konta na mobilnej nie ma klasy ehhhh */
ul.notification li {
    background-color: #0d1a2b;
    color: #61748d;
}

.info-4,
li.warning {
    background-color: #2e2709 !important;
}
.info-4 p,
li.warning {
    color: #7f6a05 !important;
}
/* */
#footer-wrapper {
    background: #222 !important;
}

.ico-ulu span,
.ico-kom span,
.votedown.active span,
.voteup.active span,
.voteup:hover span,
.votedown:hover span,
.num .orangeColor.bolder {
    color: white !important;
}



.rej-text-roz,
.rej-text-roz a,
.com-pop-up,
.list-a > li:before {
    background: #333;
}
.log-box-mes p {
    background-color: #777;
    color: #000 !important;
}

#nav h2 {
    background-color: #111;
}


.req {
    color: #b4b4b4;
}

.log-box-select-down,
.lob-box-select:hover,
.komentarze-add,
.active,
.active.checkbox,
.menu-right a:hover,
.slide-box:hover,
#nav,
h1,
h2,
h3,
.form-edit-comment,
.hover {
    background-color: #222 !important;
}

.tagCloud a {
    background-color: #222 !important;
    color: #666;
}

.list-chat li {
    background-color: #666;
}


.log-box-select-arrow-on,
.background-change,
#search h2,
.message-icon[alt="Online"] {
    display: none;
}

a,
a:visited,
.pow-user-nag,
input {
    color: #888 !important;
}


/* Przyciski */
.add,
.add-game,
.add-pocz,
.next-button,
.pagination-number,
.pagination-no-numer,
.grupy-right-submit,
.edit-comment-save,
.edit-comment-cancel,
.button-formlike,
.sendComment,
.list-e a,
.komentarze-submit,
.pagination-b a,
button,
li.next a,
li.prev a,
#fullscreen_button,
.list-chat li.a,
.button-page {
    background-color: #333 !important;
    color: #777 !important;
}


.status {
    color: #669365 !important;
}

.titleThumb {
    color: #999 !important;
    text-shadow: 1px 1px 10px #000 !important;
}



.menu.has-notifications::before,
.menu::before {
    color: #666;
}

.tagify {
    --tag-bg: #333;
    --tag-hover: #444;
}

.meu-right a,
.list-d h3,
.game-su-box h3,
form h3,
.komentarze-show-top h3,
.icon-msg,
.icon-eye,
.game-s-box h3,
.temat-box h3,
.module-b h2,
.module-b h3,
.list-c h3,
.grupa-box h3,
.dow-left-text h3,
.delete-my-comment,
.notification a,
.voteup.active,
.votedown.active,
.checkbox.active #nav h2 a {
    background-color: rgb(0, 0, 0, 0) !important;
}


* {
    border-color: #444 !important;
}


.komentarz-odp-all,
.komentarz-odp-all-hide {
    color: #9e6b00;
}


/** Przyciemnianie img i elementów, którym nie da się ustawić koloru **/
/* Poprawione ikonki ktore normalnie nie maja przezroczystosci */
.tematy-ikon,
.komentarze-add-icon {
    width: 1px;
    height: 1px;
    padding: 7px;
    filter: invert();
}

.block-user:not(:hover) {
    background: url('https://v4.jeja.pl/images/komentarz_icon_8_on.png') no-repeat left top !important;
    filter: grayscale()
}
.delete-comment:not(:hover) {
    background: url('https://v4.jeja.pl/images/komentarz_icon_7_on.png') no-repeat left top !important;
    filter: grayscale()
}
.quote-text {
    background: url('https://i.imgur.com/1Eug9AQ.png') no-repeat left top !important;
    filter: brightness(0.5)
}

img[src="https://v4.jeja.pl/images/tematy_icon_1.png"] {
    background: url('https://i.imgur.com/8yLXuqN.png') !important;
}
img[src="https://v4.jeja.pl/images/tematy_icon_2.png"] {
    background: url('https://i.imgur.com/Lbg0MUA.png') !important;
}

.icon-msg {
    background: url('https://i.imgur.com/LVnjG1Q.png') no-repeat left top;
}
.icon-msg:hover {
    background: url('https://i.imgur.com/B2pth9l.png') no-repeat left top;
}
.icon-eye,
.game-fb {
    background: url('https://i.imgur.com/WC6PXsG.png') no-repeat left top;
}
.icon-eye:hover {
    background: url('https://i.imgur.com/fftOWZz.png') no-repeat left top;
}
.komentarze-add-icon {
    background: url('https://i.imgur.com/eAtFeCe.png') !important;
    filter: brightness(0.6);
}
/* */
.menu.has-notifications::after,
.komentarz-thread-sh img {
    filter: grayscale()
}
.pow-user-icon,
#logo a {
    filter: brightness(0.5) grayscale(50%);
}
.logo {
    filter: brightness(0.4) drop-shadow(1px 1px 3px #bab8b1);
}

.box-ster-row {
    filter: invert() !important;
}

.icon-home,
.komentarze-ref-icon {
    filter: brightness(2) grayscale();
}
.file_label {
    filter: brightness(1.5) grayscale();
}
.movie-active:hover .active-arrow {
    filter: brightness(0.47) grayscale() !important;
}
a.hand-up-down {
    filter: grayscale(60%) !important;
}
.active-arrow {
    filter: brightness(0.36) grayscale()
}

.voteup,
.votedown,
.active-page,
.icon-chat {
    filter: brightness(1.6);
}

.voteup.active,
.votedown.active,
.box-autor-image,
.temat-box-new,
li.r h3::before,
.voteup:hover,
.votedown:hover,
.images-arrow-left:hover,
.images-arrow-right:hover,
.orangeColor {
    filter: brightness(0.4) !important;
}

.odz-hover .odz-boks:hover::after,
.images-arrow-left,
.images-arrow-right {
    filter: brightness(0.2) !important;
}

.odz-boks img,
.exp-smile {
    filter: brightness(0.55);
}

.ico-ulu,
.ico-kom,
.ico-f,
.ico-zgl,
.ico-create-mem,
.ico-down-template,
.footer-icon-1,
.footer-icon-2,
.pow-user-nag img,
.badge,
.badge-1,
.badge-2,
.badge-3,
.icon-user-minus,
.icon-envelope,
.icon-edit,
.icon-quote-circle,
img[src="https://v4.jeja.pl/images/pop_box_all_arrow.png"] {
    filter: brightness(0.4) grayscale(50%);
}
}
