css:
@-moz-document domain("impress.co.jp") {
body > table,
body > table > tbody > tr > td,
body > table > tbody > tr > td > table{width:auto!important;}
td:first-child[width="164"]{width:164px!important;}
td:first-child[width="127"]{width:127px!important;}
td:first-child[width="111"]{width:111px!important;}

img[src="/cda/static/image/spacer.gif"],
*[width="300"][height="300"],
*[width="300"][height="250"],
*[width="728"][height="90"],
*[width="300"][height="800"],
td:first-child[width="127"],
iframe,
object,
td.ipw_left,
#left,
#right,
body > table:first-child[cellspacing="8"][cellpadding="0"][border="0"],
colgroup
{ display:none!important; }

#main,
#content,
.contents,
.mainContents
{
  width: 100% !important;
  float: none !important;
  overflow: visible !important;
  font-size: 110% !important;
}

}
