    opacity: 0.2;
}
ytd-thumbnail:hover .ujs-links-cls.ujs-quality {
    opacity: 0.8;
}
#ujs-hdr-links-div {
    position: fixed !important;
    display: inline-block !important;
    bottom: -65px ;
    left: 7.5% ; ;
/* z-index: 500000000 !important; */
/*     visibility: hidden ; */
    visibility: visible ;
}
