    visibility: visible ;
}
#ujs-hdr-links-div:after {
    content: "👁" ;
    position: absolute ;
    height: 20px ;
    line-height: 20px ;
    top: -20px ;
    right: 20px ;
    font-size: 25px ;
    border-radius: 20px 20px 0 0 ;
    background-color: red;
    visibility: visible ;
}

