#browser-bottombox {
   min-height: 5px !important;
   -moz-appearance: toolbar !important;
   border-top-width: 1px !important;
   border-top-style: solid !important;
   border-top-color: threedhighlight !important;
   border-bottom-width: 1px !important;
   border-bottom-style: solid !important;
   border-bottom-color: threedshadow !important;
}

#browser-bottombox > #FindToolbar {
   margin-top: -2px !important;
   padding-bottom: 4px !important;
}

#browser-bottombox > #status-bar {
   margin-top: -2px !important;
}

#browser-bottombox:hover > #status-bar[moz-collapsed="true"]:not([collapsed="true"]) {
   visibility: visible !important;
}

#browser-bottombox:hover > #status-bar[moz-collapsed="true"]:not([collapsed="true"]) .statusbar-resizerpanel {
   display: none !important;
}
