css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
 url-prefix("http://www.idg.pl/news/"), 
 url-prefix("http://www.idg.pl/ocena/"),
 url-prefix("http://www.idg.pl/artykuly/"),
 url-prefix("http://bezpieczenstwo.idg.pl/"),
 url-prefix("http://gry.idg.pl/ftp/gry_")
 {

  #logomenu,
  #naglBelka,
  #drzewo,
  #szpalta,
  #idgSwiat,
  p[class=odpowiedzialnosc],
  div[class=menuGdolne],
  div[class=p],
  div[style^="padding"] {
    display: none !important;
  }

  body,
  div[style*="width: 750px"] {
   width: 600px !important;
  }

  #div_bodyhtmlall {
    width:600px !important;
  }

  #navNews {
    overflow: visible !important;
  }

  #srodekNews {
    padding:8px !important;
  }

  #zajawka {
    width: 585px !important;
    font-size: 12px !important;
  }

  #news {
    width:580px;
  }

  #dolNews {
    overflow:visible;
    width:600px;
  }

  #srodekNews h1 {
    font-size:20px !important;
    width:590px !important;
  }

  #lewabelka {
    display:none !important;
  }

  #srodek {
    margin-left: 0px !important;
  }
  
  td[style*="width: 125px"] {
    width: 1px !important;
  }

  td[style="vertical-align: top; text-align: center; width: 200px; padding-top: 10px; background-color: rgb(234, 234, 234);"] {
    width: 1px !important;
  }

  td[style="vertical-align: top; padding-left: 10px; padding-top: 0px;"] {
    padding-left: 1px !important;
  }
}
