css:
@-moz-document domain('www.jenkins.io'), regexp("^http.{0,1}://.+/jenkins/.*/(?!/.+/configure)"), regexp("^http.{0,1}://.+/jenkins/$") {
td, th, div, body, p, input {
    font-family: Calibri, Helvetica, sans-serif;
  }
  
  .tabBar .tab:first-child {
    margin-left: 5px;
  }  
  .tabBar .tab a {
    padding: 2px 4px;
    border: solid 1px #aaa !important;
    /*
    xborder-bottom: 0 !important;
    */
    border-bottom: solid 1px #aaa !important;
    position: relative;
  }
  
  .tabBar .tab.active a {
    border-bottom: solid 1px #eee !important;
  }
  
  .dashboard {
    position: relative;
  }
  
  .dashboard #projectstatus-tabBar {
    position: relative;
    top: 1px;
    z-index:1;
  }
  .dashboard #projectstatus {
    position: relative;
    z-index:0;
  }
  
  #projectstatus {
    border: solid 1px #aaa !important;
  }
  
  img.icon-lg, img.icon-md {
    width: 16px !important;
    height: 16px !important;
  }
  
  .bigtable td {
    padding: 2px 4px;
  }
  
  #projectstatus th:nth-child(1) {
    width:24px;
  }
  
  #projectstatus th:nth-child(2) {
    width:24px;
  }
  
  #breadcrumbBar > div:nth-child(3),
  #right-top-nav,
  #breadcrumbs,
  #breadcrumbs LI {
    height: 1.3em;
  }
  
  #right-top-nav,
  #breadcrumbs LI,
  #breadcrumbs LI A {
    line-height: 1.3em;
    
  }
  
  .pane-header, .pane-footer {
    padding-top: 3px;
    padding-bottom: 3px;
  }
  
  #executors th.pane {
    padding-top: 3px;
    padding-bottom: 3px;
  }
  #executors td.pane {
    padding-top: 1px;
    padding-bottom: 1px;
  }
/* Fix annoying color combination */
.node-name {
    background-color: #eeeeee !important;
}
.header {
    background-color: #eeeeee !important;
}
}

@-moz-document regexp("^http.{0,1}://.+/jenkins/.*/console.*") {
#side-panel {
    padding: 15px 5px 0px 5px!important;
    background-color: white;
    width: 21em !important;
    overflow: auto !important;
}
#side-panel,
#side-panel a {
    font-size: 0.8em !important;
    line-height: 1em !important;
}
#main-panel {
    padding: 5px 5px 0px 5px!important;
    margin-left: 17em !important;
    background-color: white;
}
#main-panel .build-caption,
#main-panel .page-headline {
    margin: 0 !important;
    padding: 0 !important;
    font-size: 1em;
    height: 20px;
}
#side-panel img,
#main-panel img {
    height: 15px !important;
    width: 15px !important;
    padding: 0 !important;
    margin: 2pt 0pt 0pt 1pt !important;
}
.console-output {
    font-family: "Droid Sans Mono", "Courier New", monospace !important;
    font-size: 9pt;
    padding: 5px 3px;
    background-color: #202020;
    color: #e8e8e8;
    border-radius: 5px;
    line-height: 1.2em;
    letter-spacing: -0.3px;
    font-weight: normal;
}
.console-output span {
    font-family: "Droid Sans Mono", "Courier New", monospace !important;
}
.console-output .warning-inline {
    color: #63a263;
}
.console-output .error-inline {
    color: #ec1212;
}
.console-output a {
    color: #95b7c1;
    font-weight: normal;
}
#menuSelector {
    background-color: #95b7c1 !important;
    color: #ec1212 !important;
}
/* i really want this to be global */
}
