css:
@-moz-document domain("utweb.trontv.com"), domain("127.0.0.1") {
.ad-card-container {
	display: none !important;
}
.media-library-top-container.show-ad {
	top: 0 !important;
}
.media-library-content-container, #media-library-toolbar {
	max-width: 1600px !important;
}
#sidebar {
	display: none !important;
}
.media-element-main {
	box-shadow: 0 2px 4px 0 rgba(0, 0, 0, .3) !important;
}
.media-element {
	border-radius: 5px !important;
}
footer {
	display: none !important;
}
}
