css:
@-moz-document domain("github.com") {
/*Dark Scrollbar*/
::-webkit-scrollbar { width: 10px; }
::-webkit-scrollbar-track { background: #fff0; }
::-webkit-scrollbar-thumb { background: #222; }
::-webkit-scrollbar-thumb:hover { background: #252525; }

body, html,

.Box-row--focus-gray.navigation-focus,

.page-responsive .previewable-comment-form .comment-form-head.tabnav,

.TimelineItem-break
{
  background: #36393F;
}

main .border-bottom
{
  background: #36393F !important;
}

.Box,

.flash,

.menu,

.auth-form-body,

.graph-before-activity-overview,

.select-menu-modal, .select-menu-item,

.subnav-item,

.filter-bar,

.timeline-comment,

.review-thread-reply .inline-comment-form,

.full-commit,
.commit-sha, .blob-code-hunk, .file-diff-split .empty-cell,

.previewable-comment-form .comment-body,

.tint-box, .capped-card-content,

.SideNav-item,

.orghead,

.py-0,


.gitzip-collect-dash 
{
    background: #2F3136;
}

input, textarea, select,

.Header, .dropdown-menu,

.bg-white,

.Box-header,

.btn,

.social-count,

.Progress,

.delete-topic-button, .delete-topic-link,

.menu-item:focus, .menu-item:hover,.menu-heading,
.bg-blue-light,

.user-status-circle-badge,
.discussion-item-icon,

.label, .Label,

.select-menu-filters, .select-menu-header,

.subnav-item:hover,

article .border-bottom,

.branch-name,

.markdown-body table tr,
.markdown-body kbd,
.markdown-body .highlight pre, .markdown-body pre,

.signed-commit-badge.verified,

.timeline-comment-header,

.gh-header .gh-header-sticky.is-stuck+.gh-header-shadow,

.commit-ref,

.file-header,
.review-thread-reply-button,

.cm-s-github-light.CodeMirror,
.cm-s-github-light .CodeMirror-lines,

.full-commit .commit-meta,
.blob-num-expandable, .blob-num-hunk,

.blankslate code,

.community-checklist .progress,

.SideNav-item[aria-current]:not([aria-current=false]), .SideNav-item[aria-selected=true], .SideNav-item:focus, .SideNav-item:hover,
.SideNav,

.table-list-header,

.TimelineItem:before,
.TimelineItem-badge,

.pinned-item-checkbox:checked+.pinned-item-name,


.gitzip-header
{
    background: #202225 !important;
}

.bg-gray-light, .bg-gray,

.topic-tag-action,
.suggester *,

.filter-item:hover,

.cm-s-github-light .CodeMirror-gutters,

.tabnav-tab.selected, .tabnav-tab[aria-current]:not([aria-current=false]), .tabnav-tab[aria-selected=true],
.page-responsive .previewable-comment-form .comment-form-head.tabnav .toolbar-commenting,

.bg-purple-light,
.js-support-banner
{
    background: #2F3136 !important;
}

.hx_avatar_stack_commit .AvatarStack-body,

.session-authentication,

.profile-timeline-year-list,

.profile-timeline-month-heading,

.header-search-current .header-search-wrapper input,

.markdown-body img,
.gh-header,

.thread-subscription-status,
.discussion-timeline-actions,

.drag-and-drop,

.AvatarStack-body,

.new-discussion-timeline .text-right,

.article-grid-toc.border-bottom.border-xl-0.pb-4.mb-5.pb-xl-0.mb-xl-0,

.d-flex.flex-wrap.bg-gray,

.previewable-comment-form,
.write-content
{
  background: #0000 !important;
}

.profile-timeline-month-heading
{
  background: #36393F !important;
}

.topic-tag,

.task-progress .progress-bar,

.markdown-body blockquote,

.Counter,

.label-select-menu .select-menu-item.navigation-focus, .label-select-menu .select-menu-item:focus, .label-select-menu .select-menu-item:hover, .label-select-menu .select-menu-item[aria-selected=true],

.flash.flash-full.flash-notice,

.drag-and-drop span, .drag-and-drop
{
    background: #18181982 !important;
}

article div[style*="background: linear-gradient(to top, rgba(255,255,255,1), rgba(255,255,255,0));"]
{
  background: linear-gradient(to top, #2e2e2f, rgba(255,255,255,0)) !important;
}

.blob-num-deletion, .blob-code-deletion .x
{
    background-color: #42151a8a;  
}
.blob-code-deletion
{
    background-color: #571f258a;  
}

.extended-markdown.warning
{
    background-color: #571f258a !important;  
}

.blob-num-addition {
    background-color: #0123088a;
}
.blob-code-addition {
    background-color: #0f451c8a;
}

.calendar-graph rect.day[fill*="graph-day-bg"],
.js-diff-placeholder
{
  fill: #202225;
}

.contrib-legend .legend li[style*="graph-day-bg"]
{
  background-color: #202225 !important;
}

text,
.dot text, circle.focus
{
  fill: #ccc;
}
.graph-canvas .axis line
{
  stroke: #ccc1;
}

body, input, textarea, select,

.text-gray-dark,

.UnderlineNav-item,

.Box-header--blue *,
.link-gray-dark,

.btn,

.social-count,

.flash,

.bg-white,

.menu-item,.menu-heading,

.session-authentication .header-logo,
.session-authentication .auth-form-header,

.filter-item,

.discussion-item-icon,

.table-list-header-toggle .btn-link,

.select-menu-filters, .select-menu-header *,
.select-menu-modal,
.select-menu-item.selected, details-menu .select-menu-item[aria-checked=true], details-menu .select-menu-item[aria-selected=true],

.dropdown-menu *,

.subnav-item,

.tabnav-tab.selected, .tabnav-tab,

.subnav-link.selected,

.markdown-body kbd,

.timeline-comment-header, .timeline-comment,

.discussion-sidebar-heading, .TimelineItem-body,

.team-mention, .user-mention,
.review-comment,

.blob-num,

.cm-s-github-light.CodeMirror,

.full-commit p.commit-title,
.full-commit .sha-block,

.text-emphasized,

.SideNav-item,

.breadcrumb strong.final-path,

.Counter,

.branch-name,

.graph-canvas .activity,

.pinned-item-name,

.markdown-body .lead-mktg p,
.token-scope,

.flash .octicon,

.commit-desc pre,


.gitzip-check-mark, .gitzip-header
{
  color: #ccc !important;
}

.blob-code-inner, .pl-s .pl-s1, .pl-smi
{
  color: #fff;
  font-weight: 800;
  text-shadow: 0 0 5px black;
}
.pl-c1, .pl-s .pl-v
{
    color: #0f3663;
  font-weight: 800;
  text-shadow: 0 0 5px black;
}

.pl-pds, .pl-s, .pl-s .pl-pse .pl-s1, .pl-sr, .pl-sr .pl-cce, .pl-sr .pl-sra, .pl-sr .pl-sre,

.cm-s-github-light .cm-string
{
    color: #3282de;
  font-weight: 800;
  text-shadow: 0 0 5px black;}
.pl-c1, .pl-s .pl-v
{
    color: #6dacf4;
  text-shadow: 0 0 5px black;
}
.pl-k {
    color: #ffaeb6;
  font-weight: 800;
  text-shadow: 0 0 5px black;
}
.pl-e, .pl-en {
    color: #b591f6;
  font-weight: 800;
  text-shadow: 0 0 5px black;
}

.link-gray,
.text-gray-light,

.text-gray,

.note,

.Subhead-description,

.vcard-username,

.muted-link,

.Label--gray, .Label--outline,

.subnav-link,

.news div.message, .news li blockquote,

.gh-header-meta,

.discussion-sidebar-item,

.task-progress,

.markdown-body blockquote,

.commit-branches *,
.full-commit .sha-block>.sha
{
  color: #959595 !important;
}

a,
.topic-tag-action,.delete-topic-button, .delete-topic-link,

a.branch-name,

.delete-topic-button, .topic-tag, .topic-tag-action,

.commit-ref,

.btn-link,

h3 a
{
  color: #94b1ff;
}

.Label--green, .Label--outline-green,
.signed-commit-badge.verified
{
  color: #53b472;
}

*,
.suggester *, .suggester,
.border-gray-light,

.bg-blue-light,

.avatar-user,

.border-bottom,
.border,
.ajax-pagination-form .ajax-pagination-btn,

.dropdown-menu:after,.dropdown-menu:before,

.timeline-comment--caret:before,.timeline-comment--caret:after,

.border-top, .border-md-top, .border-md,

.comment-form-error,

.SideNav-item,

.border-gray-dark
{
  border-color: #0000 !important;
  box-shadow: none !important;
}

.border,

.drag-and-drop
{
  border: none !important;
}

.UnderlineNav-item.selected, .UnderlineNav-item[aria-current]:not([aria-current=false]), .UnderlineNav-item[role=tab][aria-selected=true],

.subnav-primary.selected
{
    border-bottom-color: #94b1ff !important;
}

.markdown-body blockquote {
    border-left: .25em solid #18181982 !important;
}

.TimelineItem-break
{
  border-top-color: #18181982 !important
}

.dropdown-menu:after,

.avatar-user
{
  border-radius: 5px !important;
}

#js-pjax-container > .mt-4
{
  margin: 0 !important;
}

.octicon.octicon-mark-github.CircleBadge-icon.text-white
{
  fill: #000;
}

.calendar-graph text.month,
.calendar-graph text.wday
{
  fill: #ccc;
}

.pagination-loader-container,

.news .commits li img,
.CircleBadge-icon,

.github-jobs-logo strong
{
  filter: invert(1) brightness(1.5);
    mix-blend-mode: screen;
}

.pagination-loader-container .Box
{
  filter: invert(1) brightness(1.2);
}

div#network.network > div > canvas {
    -webkit-filter: invert(90%) hue-rotate(180deg)!important;
    filter: invert(90%) hue-rotate(180deg)!important;
}

#network canvas {
    background: #fff!important;
}
}
