css:
@-moz-document domain("www.youtube.com") {
BODY {
	margin: 0;
}
/*ytd-masthead #container {
	margin: 0px 20px;
	background:#3b3b3b;
	border-radius:20px !important;
	transition: all 250ms ease;
}
ytd-masthead #container:hover {
	border-radius:0px !important;
	margin: 0;
}*/
 ::-webkit-scrollbar {
	width: 10px;
}
::-webkit-scrollbar-track {
	background: transparent;
}
::-webkit-scrollbar-thumb {
	background: #4a4a4a;
}
#dismissable #details {
	min-height: 110px;
	padding: 5px 10px;
	/*background: #3a3a3a;*/
	border-radius: 15px;
	margin-top: 7px;
}
#avatar-link {
	margin-top: 3px !important;
}
#meta.ytd-rich-grid-media h3 {
	margin-top: 0;
}
#avatar-link #avatar #img, #avatar-link #avatar {
	border-radius: 5px;
}
ytd-thumbnail {
	background:#242424;
}
#thumbnail {
	background:#181818;
	border-radius:15px !important;
	overflow: hidden !important
}
yt-img-shadow #img {
	border-radius:15px
}
ytd-thumbnail-overlay-time-status-renderer {
	padding:3px 7px;
	border-radius: 100px;
	box-shadow: 0 0 10px white;
}
ytd-guide-entry-renderer #endpoint {
	width: calc(100%);
	border-radius: 20px;
	overflow:hidden;
}
ytd-guide-entry-renderer {
	margin:0 10px;
	width: calc(100% - 20px);
	border-radius: 20px
}
#content.ytd-rich-item-renderer ytd-post-renderer.ytd-rich-item-renderer, #content.ytd-rich-item-renderer ytd-shared-post-renderer.ytd-rich-item-renderer {
	border-radius:10px
}
yt-icon.ytd-badge-supported-renderer {
	color:#2c7ae8
}
ytd-rich-item-renderer {
	transition:all 250ms ease-in;
	border-radius:10px;
	overflow: hidden;
	border-radius: 20px;
	padding: 5px 5px 0px 5px;
	margin: 0 3px 25px 3px;
	background: #3a3a3a;
}
ytd-rich-item-renderer:hover {
	background: #696969;
}
ytd-thumbnail.ytd-rich-grid-media {
	border-radius: 15px;
	overflow: hidden
}
.badge-style-type-simple.ytd-badge-supported-renderer {
	color:#f00
}
.details.ytd-compact-video-renderer {
	padding:10px 0 10px 10px;
	background:#595959;
	border-radius:10px;
}
#queue-button.ytd-compact-video-renderer ytd-button-renderer.ytd-compact-video-renderer, ytd-menu-renderer.ytd-compact-video-renderer {
	margin-top:10px
}
.title.ytd-mini-guide-entry-renderer {
	font-size: 10px;
}
ytd-mini-guide-renderer div ytd-mini-guide-entry-renderer {
	border-radius: 15px;
	overflow:hidden;
	margin: 0 4px
}
#search-form #container {
	border-radius: 200px;
	margin-right: 5px;
	border: none;
	box-shadow: 0 0 4px rgba(255, 255, 255, .3);
}
#search-icon-legacy.ytd-searchbox yt-icon.ytd-searchbox {
	color:white;
	opacity:1;
}
#search-icon-legacy.ytd-searchbox {
	border-radius: 200px;
	z-index: 99999999 !important;
	box-shadow: 0 0 4px rgba(255, 255, 255, .3)
}
ytd-thumbnail-overlay-now-playing-renderer.ytd-thumbnail span {
	background : white;
	color: black;
	padding: 5px 10px;
	border-radius: 200px
}
#content-section #info-section #main-link {
	background: #3a3a3a;
	max-width: 557.350px;
	padding: 0 15px;
	border-radius: 20px;
	margin-right: 16px
}
#content-section #avatar-section {
}
yt-img-shadow.ytd-channel-renderer {
	border-radius: 20px;
}
ytd-channel-renderer[use-prominent-thumbs] #avatar-section.ytd-channel-renderer {
	min-width:140px !important
}
#text.ytd-channel-name {
	font-size: 16px
}
ytd-subscribe-button-renderer {
	flex-direction:column;
	height: 100%;
	justify-content: space-between;
	padding: 0;
	margin: 0;
	border-radius: 20px;
	align-items: space-between
}
#subscribe-button.ytd-channel-renderer {
	padding: 0;
	border-radius: 20px;
}
paper-button.ytd-subscribe-button-renderer[subscribed] {
	border-radius: 20px;
	margin: 0;
	height: 48%;
	background: #3a3a3a
}
paper-button.ytd-subscribe-button-renderer {
	height: 100%;
	border-radius: 20px;
}
#notification-preference-button {
	border-radius: 20px;
	overflow: hidden;
	height: 48%;
}
ytd-subscription-notification-toggle-button-renderer[is-icon-button][has-no-text] {
	background: #3a3a3a;
	width: 100%;
	height: 100%;
	display:flex;
	align-items: center;
	justify-content: center;
}
paper-dialog {
	border-radius: 20px;
	overflow:hidden
}
#cancel-button.yt-confirm-dialog-renderer, #confirm-button.yt-confirm-dialog-renderer {
	background: #3a3a3a;
	margin-right: 10px;
	border-radius: 7px
}
.buttons.yt-confirm-dialog-renderer {
	padding:8px 12px;
}
}
