css:
@-moz-document url-prefix("https://www.mydealz.de/"), url-prefix("https://www.promodescuentos.com/"), url-prefix("https://www.pelando.com.br/"), url-prefix("https://www.hotukdeals.com/"), url-prefix("https://www.dealabs.com/"), url-prefix("https://nl.pepper.com/"), url-prefix("https://www.preisjaeger.at/"), url-prefix("https://www.pepper.pl/"), url-prefix("https://www.chollometro.com/"), url-prefix("https://www.pepper.ru/"), url-prefix("https://www.pepper.it/") {
/*

# GRID MODE #
Brings back grid mode to Pepper

# COMPATIBLE WITH #
https://www.pepper.pl/
https://www.mydealz.de/
https://www.promodescuentos.com/
https://www.pelando.com.br/
https://www.hotukdeals.com/
https://www.dealabs.com/
https://nl.pepper.com/
https://www.preisjaeger.at/
https://www.chollometro.com/
https://www.pepper.ru/
https://www.pepper.it/

*/
 .cept-thread {
    display: block !important;
    width: 100% !important;
    margin: 0 !important;
    padding: 0 !important;
}
.listLayout {
    max-width: 108em;
}
.listLayout-side {
    max-width: 280px;
}
.listLayout-main .thread {
    display: none;
    position: relative;
    width: 230px;
    margin: 4px 2.5px;
    text-align: left;
    padding-top: 44px;
    padding-bottom: 48px;
}
.cept-thread-item {
    display: inline-flex !important;
}
.threadGrid {
    display: block !important;
    width: 100%;
    padding: 0px;
}
.threadGrid-image {
    float: unset;
    display: block;
    max-height: 200px;
    position: relative;
}
.threadGrid-image .imgFrame {
    position: unset;
}
.threadGrid-image .imgFrame img {
    max-height: 200px;
}
.tGrid-row .listLayout {
    text-align: center;
}
.threadGrid .footerMeta .iGrid-item {
    display: block;
}
.threadGrid-body .flex--fromW2 {
    display: block;
}
.threadGrid-body .flex--fromW2 .width--fromW2-6 {
    width: 100%;
}
.threadGrid-body .hide--empty {
    display: none;
}
.threadGrid-body .flex--fromW2 .btn--mode-primary {
}
.threadGrid-headerMeta {
    position: absolute;
    top: 5px;
    left: 5px;
    width: calc(100% - 10px);
}
.threadGrid .footerMeta-actionSlot button.cept-off, .threadGrid .footerMeta-actionSlot button.cept-on {
    display: none;
}
.threadGrid .footerMeta, .threadGrid .iGrid-item {
    position: unset;
}
.threadGrid .cept-dealBtn, .threadGrid .cept-tb {
    position: absolute;
    bottom: 5px;
    left: 5px;
    width: calc(100% - 10px);
}
.threadGrid .cept-description-container {
    text-overflow: ellipsis;
    overflow: hidden;
    height: 32px;
    max-height: 32px;
    line-height: 1rem;
    font-size: .75rem;
    word-break: break-word;
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    margin: 6.5px 0px;
}
.threadGrid .thread-title {
    margin-bottom: 8px;
}
.threadGrid .thread-title .cept-tt {
    font-size: .875rem;
    display: block;
    height: 40px;
    max-height: 40px;
    line-height: 1.25rem;
}
.threadGrid .cept-shipping-price .hide--toW3 {
    display: none !important;
}
.threadGrid .cept-shipping-price .hide--fromW3 {
    display: block !important;
}
.threadGrid .threadGrid-title .thread-price, .threadGrid .threadGrid-title .overflow--fade .flex--inline .mute--text, .threadGrid .threadGrid-title .overflow--fade .flex--inline .space--ml-1 {
    font-size: 1rem;
    line-height: 1.5rem;
}
.threadGrid .threadGrid-title .cept-merchant-name {
    line-height: 1.5rem;
}
.threadGrid-headerMeta .metaRibbon.hide--toW2 {
    display: none;
}
.threadGrid-headerMeta .hide--toW3 {
    display: none;
}
.threadGrid-headerMeta .hide--fromW3 {
    display: block !important;
}
.threadGrid .threadGrid-title {
    padding: 0px 8px;
}
.threadGrid .threadGrid-body {
    padding: 0px 6px;
}
.threadGrid .threadGrid-footerMeta {
    display: block;
}
.threadGrid-footerMeta .footerMeta-infoSlot {
    margin-left: 8px;
}
.threadGrid .threadGrid-image {
    margin: 8px 0px;
}
.subNavMenu-list .cept-layoutBtn-list {
    border-radius: 8px;
    border: 2px solid #eee;
    padding: 0px 8px 0px 0px;
}
.subNavMenu-list .cept-layoutBtn-list:after {
    content:'GRID';
}
.subNavMenu-list .cept-layoutBtn-text {
    display: none;
}
/* Hide banners and widgets on mydealz */
 .listLayout-main .cept-banner {
    display: none;
}
.listLayout-main .cept-widget-list {
    display: none;
}
.threadGrid .threadGrid-body .flex--fromW2 .cept-vcb {
    display: none;
}
.threadGrid-footerMeta .footerMeta-actionSlot {
    box-shadow: -10px 0 10px -3px #fff;
    z-index: 1;
}
.boxSec-div.comments-list {
    width: 320px;
    position:absolute;
    height: 600px;
    overflow: auto;
    top: 140px;
    background-color: #fff;
    z-index: 1000;
    border: 2px solid #bfbfbf;
    border-radius: 10px;
    box-shadow: 0 0 9px 0px #bfbfbf;
}
.boxSec-div.comments-list img.avatar, .boxSec-div.comments-list .comment-footer {
    display: none;
}
.footerMeta-actionSlot .cept-comment-link.btn--mode-primary {
    /* hide comments button */
}
.threadGrid-headerMeta .cept-thread-group-name {
    -webkit-line-clamp: 2;
    max-height: 40px;
    line-height: 1.25rem;
    display: -webkit-box;
    word-break: break-word;
    text-overflow: ellipsis;
    overflow: hidden;
}
.listLayout article.thread--discussion {
}
.listLayout article.thread--discussion .threadGrid .cept-description-container {
    height: 120px !important;
    max-height: 120px !important;
}
.listLayout article.thread--discussion .threadGrid .cept-vote-box {
    display: none;
}
.thread-listDiscountCell {
    display: none;
}
}
